import React from 'react'
import { Avatar, Card, Form, Table, Tag, Typography } from 'antd'
import AvatarStatus from 'components/shared-components/AvatarStatus';

const { Title } = Typography

function APR() {

    const[ list, setList ] = useState([])
    const [selectedRows, setSelectedRows] = useState([])
	const [selectedRowKeys, setSelectedRowKeys] = useState([])

    const dataSource = [
        {
            key: '1',
            name: 'Puja Srivastava',
            agent_id: 1,
            campaign_id: 32,
            language: ['English', 'Hindi']
        },
        {
            key: '2',
            name: 'Dhruva Agrawal',
            agent_id: 2,
            campaign_id: 4,
            language: ['English', 'Hindi', 'Tamil', 'Telugu', 'Gujarati']
        },
        {
            key: '3',
            name: 'Sourabh Choudhary',
            agent_id: 3,
            campaign_id: 23,
            language: ['English', 'Gujarati']
        },
        {
            key: '4',
            name: 'Aaina Khan',
            agent_id: 4,
            campaign_id: 32,
            language: ['Hindi', 'Tamil']
        },
        {
            key: '5',
            name: 'Vijay Gupta',
            agent_id: 5,
            campaign_id: 21,
            language: ['Telugu', 'Kannada']
        },
        {
            key: '6',
            name: 'Wasim Ansari',
            agent_id: 6,
            campaign_id: 20,
            language: ['Hindi', 'Gujarati', 'English']
        }
    ];

    const columns = [
        {
            title: 'Agent ID',
            key: 'agent_id',
			dataIndex: 'agent_id',
		},
		{
            title: 'User',
            key: 'name',
			dataIndex: 'name',
			render: (_, record) => (
				<div className="d-flex">
					<AvatarStatus  key={record.id} src="/img/spoctocular_short.png" name={record.name} subTitle={record.name.toLowerCase().split(' ').join('.') + "@spocto.com"}/>
				</div>
			),
			sorter: {
				compare: (a, b) => {
					a = a.name.toLowerCase();
					b = b.name.toLowerCase();
					return a > b ? -1 : b > a ? 1 : 0;
				},
			},
			editable: true
        },
        {
            title: 'Languages',
            key: 'language',
			dataIndex: 'language',
            render: tags => (
                <>
                    {tags.map(tag => {
                        let color = tag.length > 5 ? 'geekblue' : 'green';
                        if (tag === 'English') {
                            color = 'volcano';
                        }
                        return (
                            <Tag color={color} key={tag}>
                                {tag.toUpperCase()}
                            </Tag>
                        );
                    })}
                </>
              ),
            sorter: {
				compare: (a, b) => a.language.length - b.language.length,
			}
		},
    ]

    return (
        <Card>
            <Form.Item justify="center" style={{ textAlign: 'center' }}>
                <Title level={1}>Agent Productivity Report</Title>
            </Form.Item>
            <Table
                bordered
                dataSource={ list }
                columns={ columns }
                rowSelection={ rowSelection }
                onChange={ onCheck }
            />
		</Card>
    )
}

export default APR
