/* eslint-disable no-unused-vars */
import React, { useState, useEffect } from 'react'
import { Card, Table, Select, Input, Button, Badge, Menu, Tag, Pagination, message } from 'antd';
import { EyeOutlined, CloudDownloadOutlined, SearchOutlined, PlusCircleOutlined } from '@ant-design/icons';
import EllipsisDropdown from 'components/shared-components/EllipsisDropdown';
import Flex from 'components/shared-components/Flex'
import { getLangColor, getCustomerColor, getProductColor, getTransactionColor, getUserColor, getCategoryColor, getTypeColor } from 'constants/TagColor'
import utils from 'utils'
import axios from 'axios'
import { TableDownload } from 'constants/TableDownload'

const { Option } = Select

const transactionStatusList = [ 'Client Approved', 'Sent for recording', 'Recording received', 'IVR creation', 'Testing (Phase 1)', 'Testing (Phase 2)', 'Testing (Phase 3)', 'Campaign ready' ]

const fetchData = async () => await axios({
	method: 'get',
	url: 'https://68ef17ae206a41b485fa164d62a92012.us-east-1.aws.found.io:9243/sc_transaction_demo/_search?size=10000',
	auth: {
		username: 'sp_es_write',
		password: 'JxpZzs8hRV4X7'
	}
})

const changeStatus = () => {
	message.success('Status has been changed')
}

export default function SmartVoice() {

	const [ selectedRows, setSelectedRows ] = useState([])
	const [ selectedRowKeys, setSelectedRowKeys ] = useState([])
	const [ esData, setEsData ] = useState([])
	const [ list, setList ] = useState([])
	const emptyArray = []

	useEffect( () => {
		fetchData()
			.then( res => { 
				res.data.hits.hits.map( row => emptyArray.push(row._source) )
				setEsData(emptyArray)
			})
		setList(esData)
	}, [esData])


	const handleShowStatus = value => {
		if (value !== 'All') {
			const key = 'transactionStatus'
			const data = utils.filterArray(esData, key, value)
			setList(data)
		} else {
			setList(esData)
		}
	}

	const dropdownMenu = row => (
		<Menu>
			<Menu.Item>
				<Flex alignItems="center">
					<EyeOutlined />
					<span className="ml-2">View Details</span>
				</Flex>
			</Menu.Item>
			<Menu.Item>
				<Flex alignItems="center">
					<PlusCircleOutlined />
					<span className="ml-2">Add to remark</span>
				</Flex>
			</Menu.Item>
		</Menu>
	);

	const tableColumns = [
		{
			title: 'Customer Name',
			key: 'customer_name',
			dataIndex: 'customer_name',
			render: (_, record) => (
				<div className="d-flex">
					<Tag color={ getCustomerColor(record.customer_name) } style={{ letterSpacing: '1px' }}>{ record.customer_name }</Tag>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'customer_name'),
			colSpan: 1
        },
		{
			title: 'Content Name',
			key: 'content_name',
			dataIndex: 'content_name',
			render: (_, record) => (
				<div className="d-flex">
					<span>{ record.content_name }</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'content_name'),
			colSpan: 1
        },
        {
			title: 'Product',
			key: 'product',
			dataIndex: 'product',
			render: (_, record) => (
				<><Tag color={ getProductColor(record.product) }>{ record.product }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'product'),
			colSpan: 1
        },
        {
			title: 'Segment',
			key: 'segment',
			dataIndex: 'segment',
			render: (_, record) => (
				<div className="d-flex">
					<span>{ record.segment }</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'segment'),
			colSpan: 1
        },
        {
			title: 'Type',
			key: 'type',
			dataIndex: 'type',
			render: (_, record) => (
				<><Tag color={ getTypeColor(record.type) }>{ record.type }</Tag></>	
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'type'),
			colSpan: 1
        },
        {
			title: 'Level',
			key: 'level',
			dataIndex: 'level',
			render: (_, record) => (
				<div className="d-flex">
					<span>{ record.level }</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'level'),
			colSpan: 1
        },
        {
			title: 'Category',
			key: 'category',
			dataIndex: 'category',
			render: (_, record) => (
				<><Tag color={ getCategoryColor(record.category) }>{ record.category }</Tag></>	
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'category'),
			colSpan: 1
		},
		{
			title: 'Language',
			key: 'language',
			dataIndex: 'language',
			render: (_, record) => (
				<><Tag color={ getLangColor(record.language) }>{ record.language }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'language'),
			colSpan: 1
		},
		{
			title: 'Content',
			key: 'content',
			dataIndex: 'content',
			render: (_, record) => (
				<div className="d-flex">
				<span>{ record.content }</span>
			</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'content'),
			colSpan: 1
		},
		{
			title: 'Version',
			dataIndex: 'version',
			key: 'version',
			render: (_, record) => (
				<><Tag color={ record.version === 1 ? 'geekblue' : 'success' }>{ record.version }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'version'),
			colSpan: 1
		},
        {
			title: 'Transaction Status',
			key: 'status',
			dataIndex: 'status',
			render: (_, record) => (
				<><Tag color={ getTransactionColor(record.status) } onClick={ changeStatus }>{ record.status }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'status'),
			colSpan: 1
		},
        {
			title: 'Last Updated',
			key: 'last_updated_on',
			dataIndex: 'last_updated_on',
			render: (_, record) => (
				<span>{ record.last_updated_on.split(' GMT')[0] }</span>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'last_updated_on'),
			colSpan: 1
		},
		{
			title: 'Last Updated By',
			key: 'last_updated_by',
			dataIndex: 'last_updated_by',
			render: (_, record) => (
				<><Tag color={ getUserColor(record.last_updated_by) }>{ record.last_updated_by }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'last_updated_by'),
			colSpan: 1
		},
		{
			title: '',
			key: 'actions',
			dataIndex: 'actions',
			render: (_, elm) => (
				<div className="text-right">
					<EllipsisDropdown menu={ dropdownMenu(elm) }/>
				</div>
			),
			colSpan: 1
		}
	];

	const rowSelection = {
		onChange: (key, rows) => {
			setSelectedRows(rows)
			setSelectedRowKeys(key)
		}
	};

	const onSearch = e => {
		const value = e.currentTarget.value
		const searchArray = e.currentTarget.value ? list : esData
		const data = utils.wildCardSearch(searchArray, value)
		setList(data)
		setSelectedRowKeys([])
	}

	return (
		<Card>
			<Flex alignItems="center" justifyContent="between" mobileFlex={false}>
				<Flex className="mb-1" mobileFlex={false}>
					<div className="mr-md-3 mb-3">
						<Input placeholder="Search" prefix={<SearchOutlined />} onChange={e => onSearch(e)}/>
					</div>
					<div className="mb-3">
						<Select 
							defaultValue="All" 
							className="w-100" 
							style={{ minWidth: 180 }} 
							onChange={ handleShowStatus } 
							placeholder="Status"
						>
							<Option value="All">Transactions</Option>
							{ transactionStatusList.map(elm => <Option key={elm} value={elm}>{elm}</Option>) }
						</Select>
					</div>
				</Flex>
			</Flex>
                <Flex alignItems="center" mobileFlex={false}>
					<div className="mb-3 mr-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Filter</Button>
                    </div>
					<div className="mb-3 mr-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Sort</Button>
                    </div>
                    <div className="mb-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><CloudDownloadOutlined /></span>Download Selected</Button>
                    </div>
                </Flex>
			<div className="table-responsive">
                <Table 
                    bordered
					columns={ tableColumns } 
					dataSource={ esData } 
					rowKey='content_name' 
					rowSelection={{
						selectedRowKeys: selectedRowKeys,
						type: 'checkbox',
						preserveSelectedRowKeys: false,
						...rowSelection,
                    }}
                    scroll={{ x: 2000 }}
                    pagination={{ pageSize: 10 }}
                    showQuickJumper
					responsive={true}
				/>
			</div>
		</Card>
	)
}
