import React, { useEffect, useState } from 'react'
import { Card, Tag, Table, Form, Select, Button, Checkbox, Row, Col, Typography, Input, message } from 'antd';
import { CloseCircleOutlined, SmileTwoTone  , SearchOutlined } from '@ant-design/icons'
import Flex from 'components/shared-components/Flex'
import AvatarStatus from 'components/shared-components/AvatarStatus';
import agentData from "assets/data/agent-list.data.json";
import { DataGrid } from '@material-ui/data-grid'
import utils from 'utils'

const { Title } = Typography;
const { Option } = Select;
const { TextArea } = Input

const AGENT_ASSIGNED_API_URL = 'http://dev2.spoctocular.com/test_api/public/api/agent_assignedlist'
const AGENT_SUBMIT_API_URL = 'http://dev2.spoctocular.com/test_api/public/api/assign_agent'
const CAMPAIGNS_API_URL = 'http://dev2.spoctocular.com/test_api/public/api/campaigns'
const AGENT_DETAILS_API_URL = 'http://dev2.spoctocular.com/test_api/public/api/agentdetails'

function CampaignAssigment(props) {

    const[ camps, setCamps ] = useState([])
    const[ agents, setAgents ] = useState([])
    const[ list, setList ] = useState([])
    const [selectedRows, setSelectedRows] = useState([])
	const [selectedRowKeys, setSelectedRowKeys] = useState([])
    const [ objLabel, setLabel ] = useState()
    const [ visible, setVisible ] = useState(false)
    const [ checked, setChecked ] = useState(false)
    const checkedLanguages = []

    useEffect(() => {
        fetchAPI_Campaigns(CAMPAIGNS_API_URL)
        fetchAPI_Agents_Assigned(AGENT_ASSIGNED_API_URL)
    }, [])

    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    };

    const dataSource = [
        {
            key: '1',
            name: 'Puja Srivastava',
            agent_id: 1,
            campaign_id: 32,
            language: ['English', 'Hindi']
        },
        {
            key: '2',
            name: 'Dhruva Agrawal',
            agent_id: 2,
            campaign_id: 4,
            language: ['English', 'Hindi', 'Tamil', 'Telugu', 'Gujarati']
        },
        {
            key: '3',
            name: 'Sourabh Choudhary',
            agent_id: 3,
            campaign_id: 23,
            language: ['English', 'Gujarati']
        },
        {
            key: '4',
            name: 'Aaina Khan',
            agent_id: 4,
            campaign_id: 32,
            language: ['Hindi', 'Tamil']
        },
        {
            key: '5',
            name: 'Vijay Gupta',
            agent_id: 5,
            campaign_id: 21,
            language: ['Telugu', 'Kannada']
        },
        {
            key: '6',
            name: 'Wasim Ansari',
            agent_id: 6,
            campaign_id: 20,
            language: ['Hindi', 'Gujarati', 'English']
        }
    ];

    const columns = [
        {
            title: 'Agent ID',
            key: 'agent_id',
			dataIndex: 'agent_id',
		},
		{
            title: 'User',
            key: 'name',
			dataIndex: 'name',
			render: (_, record) => (
				<div className="d-flex">
					<AvatarStatus  key={record.id} src="/img/spoctocular_short.png" name={record.name} subTitle={record.name.toLowerCase().split(' ').join('.') + "@spocto.com"}/>
				</div>
			),
			sorter: {
				compare: (a, b) => {
					a = a.name.toLowerCase();
					b = b.name.toLowerCase();
					return a > b ? -1 : b > a ? 1 : 0;
				},
			},
			editable: true
        },
        {
            title: 'Languages',
            key: 'language',
			dataIndex: 'language',
            render: tags => (
                <>
                    {tags.map(tag => {
                        let color = tag.length > 5 ? 'geekblue' : 'green';
                        if (tag === 'English') {
                            color = 'volcano';
                        }
                        return (
                            <Tag color={color} key={tag}>
                                {tag.toUpperCase()}
                            </Tag>
                        );
                    })}
                </>
              ),
            sorter: {
				compare: (a, b) => a.language.length - b.language.length,
			}
		},
    ]

    function fetchAPI_Campaigns(url) {
        fetch(url)
            .then( response => response.json() )
            .then( res => setCamps(res.campaigns))
    }

    function fetchAPI_Agents_Assigned(url) {
        fetch(url)
            .then( response => response.json() )
            .then( res => setAgents(res.agentassigned))
    }

    function loadServerRows() {
        new Promise((resolve, reject) => {
            fetch(AGENT_DETAILS_API_URL)
            .then( response => response.json() )
            .then( res => res.agentdetails )
            // .then(data => {
            //     console.log(data)
            //     resolve({
            //         rows: data
            //     });
            //     resolve({
            //         rows: data.map((row) => ({ key: row.id, ...row }))
            //     });
            // })
            .catch((error) => reject(error));
        });
    }

    const renderCampaignName = () => {

        return (
            <>
                {camps.map( name => (
                    <Option value={ name.id }>{ name.name }</Option>
                ))}  
            </>   
        )
    }

    const selectCampaignOption = value => {
        setVisible(true)
        setLabel(value.label)
        setList(dataSource)
        // const { rows } = async () => await loadServerRows()
        // console.log(rows)
        // setDetails(rows)
    }

    // const renderCampaignId = () => {

    //     return (
    //         <>
    //             {camps.map( name => {
    //                 name.name === 'Demo - DEBT Collect' ? <Tag color="red">20</Tag> :
    //                 name.name === 'Demo - Payment Reminder' ? <Tag color="red">21</Tag> :
    //                 name.name === 'Demo - Legal Reminder' ? <Tag color="red">22</Tag> :
    //                 <Tag color="red">32</Tag>
    //             })}
    //         </>   
    //     )
    // }

    const onCheck = event => {
        checkedLanguages.push(event.target.value)
        // let x = checkedLanguages.toString()
        // console.log(x)
    }

    const renderLanguages = () => {
        const languages = ['English', 'Hindi', 'Tamil', 'Telugu', 'Kannada', 'Marathi', 'Gujarati', 'Bengali', 'Punjabi', 'Malayalam']

        return (
            <>
                { languages.map( language => (
                    <Col span={6}>
                        <Checkbox value={ language } style={{ lineHeight: '32px' }} onChange={ onCheck }>{ language }</Checkbox>
                    </Col>
                )) }
            </>
        )
    }

    const rowSelection = {
		onChange: (key, rows) => {
			setSelectedRows(rows)
			setSelectedRowKeys(key)
		}
	};

	function onSearch(e) {
        // let value
        // if (checkedLanguages.length == 0) {
        //     value = e.currentTarget.value
        //     // console.log(value)
        // } else {
        //     value = checkedLanguages
        // }
        // console.log(value)
        const value = e.currentTarget.value
        const searchArray = e.currentTarget.value ? list : dataSource
        const data = utils.wildCardSearch(searchArray, value)
        setList(data)
        setSelectedRowKeys([])
	}

    const handleSubmit = e => {

    message.success(`Your response has been registered and is now being proceesed ðŸ¥³`);

        // If you want to send dynamic data received from checkbox, uncomment the line below and comment out Line 252
        // const data = selectedRows
        // console.log(data)
        const data = { agent_id: 2, campaign_id: 4 }
        fetch(AGENT_SUBMIT_API_URL, {
            method: 'POST',
            body: data
        })
        .then( res => console.log(res.status ))

        e.preventDefault()

    }

    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    };

    return (
        <Card>
            <Title style={{ textAlign: 'center', fontSize: '2rem', marginTop: '2%', marginBottom: '4%' }}>Campaign Assignment</Title>
            <Form
                name="validate_other"
                {...formItemLayout}
                onFinish={onFinish}
                textAlign="center"
                initialValues={{ rate: 3.5 }}
            >
                <Form.Item style={{ margin: '0', position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
                    <Flex mobileFlex={false}>
                        <div className="mb-3 mr-3">
                            <Button type="primary" onClick={() => props.history.push("/app/home")}><span style={{ marginRight: '0.5rem' }}><CloseCircleOutlined /></span>Close</Button>
                        </div>
                    </Flex>
                </Form.Item>
                <Form.Item
                    name="select"
                    label="Campaign Name"
                    hasFeedback
                    rules={[{ required: true, message: 'Please select a campaign name' }]}
                >
                    <Select labelInValue placeholder="Please select a campaign name" onChange={ selectCampaignOption }>
                        { renderCampaignName() }
                    </Select>
                </Form.Item>
                { visible && 
                    <>  
                        {/* <Form.Item name="tag" label="Campaign ID" hasFeedback>
                            { renderCampaignId() }
                        </Form.Item> */}
                        {/* <Form.Item name="language" label="Language">
                            <Checkbox.Group className="pl-3">
                                <Row>
                                    { renderLanguages() }
                                </Row>
                            </Checkbox.Group>
                        </Form.Item> */}
                        <div className="mr-md-3 mb-3">
						    <Input placeholder="Search" prefix={<SearchOutlined />} onChange={e => onSearch(e)} style={{ width: '60%', marginLeft: '25%' }} />
					    </div>
                        <Table
                            bordered
                            dataSource={ list }
                            columns={ columns }
                            rowSelection={ rowSelection }
                            onChange={ onCheck }
                        />
                        <Form.Item style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}} className="mt-5">
                            <Button variant="contained" type="primary" onClick={ handleSubmit } style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }}>Submit</Button>
                        </Form.Item>
                    </>
                }
                {/* { console.log(checkedLanguages) }
                { console.log(details) } */}
                {/* { console.log(dataSource) } */}
            </Form>
        </Card>
    )
}

export default CampaignAssigment
