import React from 'react'
import { Card, Form, Table, Tabs, Tag, Typography, Space, Button, message } from 'antd'
import { LinkOutlined } from '@ant-design/icons'
import AvatarStatus from 'components/shared-components/AvatarStatus';

const { TabPane } = Tabs;
const { Title } = Typography

function APR() {

    const handleLink = () => {
        message.success('Download of your chat history will begin shortly');
    }
    const dataSource = [
        {
            key: '1',
            name: 'Puja Srivastava',
            agent_id: 1,
            campaign_id: 32,
            chatCount: 5,
            chatDuration: '0:10:30',
            customerName: 'Bhavin',
            customerPhone: '99XXXX7008',
            loanAccNo: 'LPXXXX1234',
            chatInTime: '13:00:00',
            chatOutTime: '13:02:15',
            totalChatDuration: '0:02:15',
            botTime: '0:01:10',
            language: ['English', 'Hindi']
        },
        {
            key: '2',
            name: 'Dhruva Agrawal',
            agent_id: 2,
            campaign_id: 4,
            chatCount: 10,
            chatDuration: '1:05:15',
            customerName: 'Sumeet',
            customerPhone: '99XXXX7008',
            loanAccNo: 'LPXXXX1234',
            chatInTime: '13:30:00',
            chatOutTime: '13:34:55',
            totalChatDuration: '0:04:55',
            botTime: '0:02:45',
            language: ['English', 'Hindi', 'Tamil', 'Telugu', 'Gujarati']
        },
        {
            key: '3',
            name: 'Sourabh Choudhary',
            agent_id: 3,
            campaign_id: 23,
            chatCount: 2,
            chatDuration: '0:02:45',
            customerName: 'Kapil',
            customerPhone: '99XXXX7008',
            loanAccNo: 'LPXXXX1234',
            chatInTime: '12:32:00',
            chatOutTime: '12:32:45',
            totalChatDuration: '0:00:45',
            botTime: '0:02:15',
            language: ['English', 'Gujarati']
        },
        {
            key: '4',
            name: 'Aaina Khan',
            agent_id: 4,
            campaign_id: 1,
            chatCount: 5,
            chatDuration: '1:05:10',
            customerName: 'Namita',
            customerPhone: '99XXXX7008',
            loanAccNo: 'LPXXXX1234',
            chatInTime: '09:00:00',
            chatOutTime: '09:45:45',
            totalChatDuration: '0:05:45',
            botTime: '0:01:10',
            language: ['Hindi', 'Tamil']
        },
        {
            key: '5',
            name: 'Vijay Gupta',
            agent_id: 5,
            campaign_id: 21,
            chatCount: 10,
            chatDuration: '0:30:00',
            customerName: 'Shalini',
            customerPhone: '99XXXX7008',
            loanAccNo: 'LPXXXX1904',
            chatInTime: '11:00:00',
            chatOutTime: '11:01:05',
            totalChatDuration: '0:01:05',
            botTime: '0:03:30',
            language: ['Telugu', 'Kannada']
        },
        {
            key: '6',
            name: 'Wasim Ansari',
            agent_id: 6,
            campaign_id: 20,
            chatCount: 3,
            chatDuration: '0:42:20',
            customerName: 'Rahul',
            customerPhone: '99XXXX7890',
            loanAccNo: 'LPXXXX1174',
            chatInTime: '13:18:00',
            chatOutTime: '13:22:15',
            totalChatDuration: '0:04:15',
            botTime: '0:02:00',
            language: ['Hindi', 'Gujarati', 'English']
        }
    ];


    const columns_dashboard = [
        {
            title: 'Agent ID',
            key: 'agent_id',
			dataIndex: 'agent_id',
		},
		{
            title: 'Agent Name',
            key: 'name',
			dataIndex: 'name',
			render: (_, record) => (
				<div className="d-flex">
					<AvatarStatus  key={record.id} src="/img/spoctocular_short.png" name={record.name} subTitle={record.name.toLowerCase().split(' ').join('.') + "@spocto.com"}/>
				</div>
			),
			sorter: {
				compare: (a, b) => {
					a = a.name.toLowerCase();
					b = b.name.toLowerCase();
					return a > b ? -1 : b > a ? 1 : 0;
				},
			},
        },
        {
            title: 'Chat Count',
            key: 'chatCount',
            dataIndex: 'chatCount',
            render: count => (
				<Tag className ="text-capitalize" color={ count > 5 ? 'cyan' : 'red' }>{ count }</Tag>
			),
			sorter: {
				compare: (a, b) => a.chatCount.length - b.chatCount.length,
			}
        },
        {
            title: 'Total Chat Duration',
            key: 'chatDuration',
            dataIndex: 'chatDuration'
		},
    ]

    const columns_data = [
        {
            title: 'Customer Name',
            key: 'customerName',
			dataIndex: 'customerName',
        },
        {
            title: 'Customer Phone Number',
            key: 'customerPhone',
			dataIndex: 'customerPhone',
        },
        {
            title: 'Loan Account Number',
            key: 'loanAccNo',
			dataIndex: 'loanAccNo',
		},
		{
            title: 'Agent Name',
            key: 'name',
			dataIndex: 'name',
        },
        {
            title: 'Chat In-Time',
            key: 'chatInTime',
			dataIndex: 'chatInTime',
        },
        {
            title: 'Chat Out-Time',
            key: 'chatOutTime',
			dataIndex: 'chatOutTime',
        },
        {
            title: 'Chat Duration',
            key: 'totaChatDuration',
			dataIndex: 'totalChatDuration',
        },
        {
            title: 'Bot Chat Time',
            key: 'botTime',
			dataIndex: 'botTime',
        },
        {
            title: 'Chat History',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <a href="#" onClick={ handleLink }>
                        <LinkOutlined style={{ color: 'blue', fontSize: '1.5rem' }} />
                    </a> 
                </Space>
            ),
        }
    ]

    function callback(key) {
        console.log(key);
    }

    return (
        <Card>
            <Form.Item justify="center" style={{ textAlign: 'center', marginTop: '2%' }}>
                <Title level={1}>Agent Productivity Report</Title>
            </Form.Item>
            <Tabs defaultActiveKey="1" onChange={ callback }>
                <TabPane tab="Agent Dashboard" key="1">
                <Table
                    bordered
                    dataSource={ dataSource }
                    columns={ columns_dashboard }
                />
                </TabPane>
                <TabPane tab="Agent Data" key="2">
                <Table
                    bordered
                    dataSource={ dataSource }
                    columns={ columns_data }
                />
                </TabPane>
            </Tabs>
		</Card>
    )
}

export default APR
