import React from 'react'
import { SIDE_NAV_WIDTH, SIDE_NAV_COLLAPSED_WIDTH, NAV_TYPE_TOP } from 'constants/ThemeConstant';
import { APP_NAME } from 'configs/AppConfig';
import { connect } from "react-redux";
import utils from 'utils';
import { Grid } from 'antd';

const { useBreakpoint } = Grid;

const getLogoWidthGutter = (props, isMobile) => {
  const { navCollapsed, navType } = props;
  const isNavTop = navType === NAV_TYPE_TOP ? true : false
  if(isMobile && !props.mobileLogo) {
    return 0
  }
  if(isNavTop) {
    return 'auto'
  }
  if(navCollapsed) {
    return `${SIDE_NAV_COLLAPSED_WIDTH}px`
  } else {
    return `${SIDE_NAV_WIDTH}px`
  }
}

const getLogo = (props) => {
  const { navCollapsed, logoType } = props;
  if (logoType === 'light') {
    if (navCollapsed) {
      return 'https://mpng.subpng.com/20180611/rrg/kisspng-icici-bank-state-bank-of-india-banking-in-india-ba-5b1f39b27e6008.2584101615287730425176.jpg'
    }
    return 'https://mpng.subpng.com/20180611/rrg/kisspng-icici-bank-state-bank-of-india-banking-in-india-ba-5b1f39b27e6008.2584101615287730425176.jpg'
  } 

  
  if (navCollapsed) {
    return 'https://mpng.subpng.com/20180611/rrg/kisspng-icici-bank-state-bank-of-india-banking-in-india-ba-5b1f39b27e6008.2584101615287730425176.jpg'
  }
  return 'https://buy.icicibank.com/static/media/icici_bank_logo_symbol.be416e8e.png'
}

const getLogoDisplay = (isMobile, mobileLogo) => {
  if(isMobile && !mobileLogo) {
    return 'd-none'
  } else {
    return 'logo'
  }
}

export const Logo = (props) => {
  const isMobile = !utils.getBreakPoint(useBreakpoint()).includes('lg')
  return (
    <div
      className={getLogoDisplay(isMobile, props.mobileLogo)} 
      style={{width: `${getLogoWidthGutter(props, isMobile)}`}}>
      <img src={getLogo(props)} alt={`${APP_NAME} logo`} style={{ width: '30px', height: '30px'}} />
    </div>
  )
}

const mapStateToProps = ({ theme }) => {
  const { navCollapsed, navType } =  theme;
  return { navCollapsed, navType }
};

export default connect(mapStateToProps)(Logo);
