import React, { useState } from 'react'
import { useHistory } from 'react-router'
import { InboxOutlined } from '@ant-design/icons';
import { Form, Button, Upload, Typography, message } from 'antd';
import axios from 'axios'
import FileMapping from 'assets/functions/FileMapping'
import XLSX from 'xlsx'

const { Title } = Typography
const { Dragger } = Upload

const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

const dummyRequest = ({ file, onSuccess }) => {
    setTimeout(() => {
        onSuccess("ok");
    }, 0);
};

const normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
};

const UploadTransactions = props => {

    const { push } = useHistory()

    const [ data, setData ] = useState([])
    const [ cols, setCols ] = useState([])
    const [ selectedFile, setSelectedFile ] = useState(null)
    const [ selectedFileList, setSelectedFileList ] = useState([])
    const [ nextState, setNextState ] = useState({})

    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    }

    const onChange = info => {
        switch (info.file.status) {
            case "uploading":
                nextState.selectedFileList = [info.file]
                break
            case "done":
                nextState.selectedFile = info.file
                nextState.selectedFileList = [info.file]
                break
            default:
                // error or removed
                nextState.selectedFile = null
                nextState.selectedFileList = []
        }
        setNextState(nextState)
    };

    const onFileChange = e => {
        let file = e.fileList[0]
        console.log(file)
    }

    const onFileUpload = e => {
        const formData = new FormData()

        formData.append('spoctoClientData', selectedFile, selectedFile.name)

        console.log(selectedFile)

        axios.post()
    }

    const fileData = () => { 

        if ( selectedFile ) { 

          return ( 
            <div> 
              <h2>File Details:</h2> 
              <p>File Name: { selectedFile.name }</p> 
              <p>File Type: { selectedFile.type }</p> 
              <p> 
                Last Modified:{" "} 
                { selectedFile.lastModifiedDate.toDateString() } 
              </p> 
            </div> 
          )
        } 
        else { 
          return ( 
            <div> 
              <br /> 
              <h4>Choose before Pressing the Upload button</h4> 
            </div> 
          ) 
        } 
    };

    // --- Reading and parsing Excel File --- //

    // const handleFile = file => {

    //     const reader = new FileReader()
    //     const rABS = !!reader.readAsBinaryString;

    //     reader.onload = e => {
    //         const bstr = e.target.result
    //         const wb = XLSX.read(bstr, { type: rABS ? 'binary' : 'array' })

	// 	    /* Get first worksheet */
	// 	    const wsname = wb.SheetNames[0];
	// 	    const ws = wb.Sheets[wsname];
    //         // let first_worksheet = workbook.Sheets[workbook.SheetNames[0]];
    //         const data = XLSX.utils.sheet_to_json(ws, { header:1 });
    //         setData(data)
    //         setCols(make_cols(ws['!ref']))
    //     }

    //     if (rABS) reader.readAsBinaryString(file)
    //     else reader.readAsArrayBuffer(file)
    // }

    return (
        <Form
            name="validate_other"
            {...formItemLayout}
            onFinish={ onFinish }
            initialValues={{ rate: 3.5 }}
        >
            <Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
            <Form.Item justify="center">
                {/* <input type="file" accept=".csv, .xlsx" onChange={ e => handleFileChosen(e.target.files[0]) } /> */}
                <Form.Item name="excelFileUpload" valuePropName="fileList" getValueFromEvent={ normFile } noStyle>
                    {/* <Dragger name="files" action="https://gofile.io/uploadFiles">
                        <p className="ant-upload-drag-icon">
                            <InboxOutlined />
                        </p>
                        <p className="ant-upload-text">Click or drag file to this area to upload</p>
                        <p className="ant-upload-hint">Support for single and bulk upload.</p>
                    </Dragger> */}
                    <Dragger name="files" action="" fileList={ selectedFileList } customRequest={ dummyRequest } onChange={ onChange }>
                        <p className="ant-upload-drag-icon">
                            <InboxOutlined />
                        </p>
                        <p className="ant-upload-text">Click or drag file to this area to upload</p>
                        <p className="ant-upload-hint">Support for single and bulk upload.</p>
                    </Dragger>
                    {/* <div class="file-upload-wrapper">
                        <input type="file" id="input-file-now-custom-2" class="file-upload" data-height="500" />
                    </div> */}
                </Form.Item>
            </Form.Item>
            <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="mt-5">
                <Button onClick={ /*() => push("app/journeys/dashboard")*/ onFileUpload } variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }}>Create Transactions</Button>
            </Form.Item>
            { fileData() }
      </Form>
    );
};  

export default UploadTransactions
