import React from 'react'
import { Input, Card, Typography, Form, Checkbox } from 'antd';
import { Link } from 'react-router-dom'

const { Search } = Input
const { Title, Paragraph } = Typography


export default function UniversalSearch() {

    const onSearch = value => console.log(value);

    function onChange(e) {
        console.log(`checked = ${e.target.checked}`);
    }

    return (
        <Card>
            <Form>
                <Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1.5%' }}>
                    <Title>Spocto Content Search</Title>
                </Form.Item>
                <Form.Item>
                    <Search 
                        placeholder="Input Search Text" 
                        onSearch={ onSearch } 
                        style={{ width: '100%' }}                     
                        allowClear
                        enterButton
                        size="large" 
                    />
                    <Paragraph className="mt-2 ml-2" style={{ fontSize: '0.65rem'}}>Type Client Name, Language, Product, Category etc... e.g. ICICI Bank, Axis Bank or English, Hindi or Loan, Credit Card or Basic, Detailed</Paragraph>
                    <Paragraph className="mt-2 ml-2"><Checkbox onChange={ onChange } />Please check this box to indicate that you are authorized to use Spocto search tool and agree to <Link>terms and condition</Link>.</Paragraph>
                </Form.Item>
            </Form>
        </Card>
    )
}
