import React, { useState } from 'react'
import { Form, Select, Button, Checkbox, Row, Col, Typography, Input, Tag } from 'antd';
import { getLangColor, getCustomerColor, getChannelColor, getTypeColor, getProductColor, getSegmentColor } from 'constants/TagColor'
import { StyledButton, StyledNavButton } from 'constants/StyledComponents'

const { Option } = Select;
const { TextArea } = Input

const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

const FormOptions = props => {
    const onFinish = (values) => {
      console.log('Received values of form: ', values);
    }

    const [ languages, setLanguages ] = useState([])
    const [ customer, setCustomer ] = useState()
    const [ channel, setChannel ] = useState()
    const [ product, setProduct ] = useState()
    const [ segment, setSegment ] = useState()

    const onSelectCustomer = event => setCustomer(event)

    const onSelectChannel = event => setChannel(event)

    const onSelectProduct = event => setProduct(event)

    const onSelectSegment = event => setSegment(event)

    const onSelectLanguages = event => setLanguages(event)

    const renderClients = () => {
        const clients = ['AXIS Bank', 'ICICI Bank', 'RBL Bank', 'TVS', 'SBI Bank', 'IDFC Bank', 'Bajaj Auto Finance', 'TCL', 'CBD', 'ENBD', 'TCHFL', 'Kotak Mahindra', 'Avanse']

        return (
            <>
                { clients.map( client => (
                    <Option value={ client.split(' ')[0].toLowerCase() }>{ client }</Option>
                )) }    
            </>
        )
    }

    const renderLanguages = () => {
        const languages = ['English', 'Hindi', 'Tamil', 'Telugu', 'Kannada', 'Marathi', 'Gujarati', 'Bengali', 'Punjabi', 'Malayalam']

        return (
            <>
                <Select mode="multiple" showArrow style={{ width: '100%' }} onChange={ onSelectLanguages } placeholder="Please select the languages">
                    { languages.map( language => (
                        <Option value={ language } style={{ lineHeight: '32px', width: '100%' }}>
                            <Tag color={ getLangColor(language) }>{ language }</Tag>
                        </Option>
                    )) }
                </Select>
            </>
        )
    }

    const renderProducts = () => {
        const products = ['Loan', 'Credit Card', 'MFI', 'EMI On Debit Card', 'Agri Loan', 'Tractor Loan', 'CD-SNC', 'Common Borrowers', 'Pay Later']

        return (
            <>
                { products.map( product => (
                    <Option value={ product }>{ product }</Option>
                )) }    
            </>   
        )
    }

    const renderSegments = () => {
        const segments = ['Pre Due', 'Post Due', 'Bucket X', 'NPA', 'W/Off', 'Phase 1', 'Phase 2', 'Phase 3', 'Phase 4 (Pre 90)', 'Phase 4 (90+)']

        return (
            <>
                {segments.map( segment => (
                    <Option value={ segment }>{ segment }</Option>
                ))}    
            </>  
        )
    }

    const renderTypes = () => {
        const types = ['Reminder', 'Impact', 'Legal', 'Follow up', 'Relationship', 'Action', 'Urgency']

        return (
            <>
                {types.map( type => (
                    <Option value={ type }>{ type }</Option>
                ))}    
            </>  
        )
    }

    const renderLevels = () => {
        const levels = [1, 2, 3, 4, 5, 6]

        return (
            <>
                { levels.map( level => (
                    <Option value={ level }>{ level }</Option>
                )) }    
            </>  
        )
    }

    const renderCategories = () => {
        const categories = ['Basic', 'Detailed', 'Urgency', 'Charges', 'Clean Track', 'Good', 'CIBIL', 'Credit', 'Action', 'NC_1', 'NC_2', 'Awareness', 'Soft Reminder', 'Collection Reminder', 'Impact Reminder 1', 'Impact Reminder 2', 'Good Order', 'Long Lasting', 'Relationship Status', 'Avoid Action', 'Action To Be Initiated', 'Action Initiated', 'Degradation Of Account', 'Legal Inbound', 'Legal', 'Reminder', 'PTP Reminder', 'Broken PTP', 'Default', 'Delinquent', 'Legal Reminder', 'Legal Action', 'Legal To Be Initiated', 'Other', 'Reacted', 'Reaction']

        return (
            <>
                { categories.map( category => (
                    <Option value={ category }>{ category }</Option>
                )) }    
            </>  
        )
    }

    return (
        <Form
            name="validate_other"
            { ...formItemLayout }
            onFinish={ onFinish }
            textAlign="center"
            initialValues={{ rate: 3.5 }}
        >
            <Form.Item
                name="select"
                label="Client Name"
                hasFeedback
                rules={[{ required: true, message: 'Please select a client' }]}
            >
                <Select placeholder="Please select a client" onChange={ onSelectCustomer }>
                    { renderClients() }
                </Select>
            </Form.Item>

            <Form.Item
                name="channel"
                label="Channel"
                hasFeedback
                rules={[{ required: true, message: 'Please select a channel' }]}
            >
                <Select placeholder="Please select the channel" onChange={ onSelectChannel }>
                    <Option value="ivr">smartVoice</Option>
                    <Option value="sms">smartText</Option>
                    <Option value="email">smartEmail</Option>
                </Select>
            </Form.Item>

            <Form.Item name="language" label="Languages">
                { renderLanguages() }
            </Form.Item>

            <Form.Item
                name="product"
                label="Product"
                hasFeedback
                rules={[{ required: true, message: 'Please select a product' }]}
            >
                <Select placeholder="Please select a product">
                    { renderProducts() }
                </Select>
            </Form.Item>

            <Form.Item
                name="segment"
                label="Segment"
                hasFeedback
                rules={[{ required: true, message: 'Please select a segment' }]}
            >
                <Select placeholder="Please select a segment">
                    { renderSegments() }
                </Select>
            </Form.Item>

            <Form.Item
                name="type"
                label="Type"
                hasFeedback
                rules={[{ message: 'Please select a type' }]}
            >
                <Select placeholder="Please select a type">
                    { renderTypes() }
                </Select>
            </Form.Item>

            <Form.Item
                name="level"
                label="Level"
                hasFeedback
                rules={[{ message: 'Please select a level' }]}
            >
                <Select placeholder="Please select a level">
                    { renderLevels() }
                </Select>
            </Form.Item>

            <Form.Item
                name="category"
                label="Category"
                hasFeedback
                rules={[{ required: true, message: 'Please select a category' }]}
            >
                <Select placeholder="Please select a category">
                    { renderCategories() }
                </Select>
            </Form.Item>

            <Form.Item
                name="content"
                label="Content"
                rules={[{ required: true, message: 'Please enter the content' }]}
            >
                <TextArea rows={4} />
            </Form.Item>

            <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginLeft: '20%' }} className="mt-5">
                <StyledButton variant="contained" type="primary" style={{ width: '80%', height: '3rem', fontSize: '1.25rem', textAlign: 'center', alignItems: 'center' }}>Upload the transaction</StyledButton>
            </Form.Item>
        </Form>
    )
};  

export default FormOptions
