import React, { useState } from 'react'
import { message, Typography, Form, Button, Checkbox, Row, Col } from 'antd'
import axios from 'axios'
import { FILE_UPLOAD_URL } from 'constants/ApiConstant'

const { Title } = Typography

const FileUploadForm = props => {

    const [ file, setFile ] = useState(null)
    const [ visible, setVisible ] = useState(false)
    const [ checked, setChecked ] = useState(false)
    const checkedLanguages = []

    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    };

    const onCheck = event => {
        checkedLanguages.push(event.target.value)
        let x = checkedLanguages.toString()
        console.log(x)
    }

    const renderLanguages = () => {
        const languages = ['English', 'Hindi', 'Tamil', 'Telugu', 'Kannada', 'Marathi', 'Gujarati', 'Bengali', 'Punjabi', 'Malayalam']

        return (
            <>
                { languages.map( language => (
                    <Col span={6}>
                        <Checkbox value={ language } style={{ lineHeight: '32px' }} onChange={ onCheck }>{ language }</Checkbox>
                    </Col>
                )) }
            </>
        )
    }

    const onSubmit = async (e) => {
        let res = await uploadFile(file);
        console.log(res.data);
	}

    const onChange = e => {
        // setFile(e.target.files[0])

        let input = e.target.files[0];

        let reader = new FileReader()
        console.log(reader)

        reader.onload = function(e) {
            console.log(e.target.result)
        }

        reader.readAsText(input)

	}

    const uploadFile = async (file) => {

        const formData = new FormData();

        formData.append('file', file)

        let res =  await axios.post(FILE_UPLOAD_URL, formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
		})
		// res.then( () => message.success(`Your ${file.name} was uploaded successfully.`) )
		// .catch( err => message.error(`A ${err.status} error occured. Please try again.`) )

		// console.log(res)
		return res
	}

	return (
        <Form
            name="validate_other"
            {...formItemLayout}
            onFinish={ onSubmit }
            textAlign="center"
            initialValues={{ rate: 3.5 }}
        >
			<Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
			<input type="file" onChange={ onChange } />
            <Form.Item name="language" label="Language">
                <Checkbox.Group className="pl-3">
                    <Row>
                        { renderLanguages() }
                    </Row>
                </Checkbox.Group>
            </Form.Item>
			<Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="mt-5">
                <Button htmlType="submit" variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }}>Create Transactions</Button>
            </Form.Item>
		</Form>
	)

}
export default FileUploadForm
