/* eslint-disable no-unused-vars */
import React, { useState, useEffect } from 'react'
import { Card, Table, Select, Input, InputNumber, Button, Row, Col, Menu, Tag, Modal, message, Space } from 'antd';
import { CloudDownloadOutlined, SearchOutlined, PlusCircleOutlined } from '@ant-design/icons';
import Highlighter from 'react-highlight-words';
import EllipsisDropdown from 'components/shared-components/EllipsisDropdown';
import Flex from 'components/shared-components/Flex'
import { getLangColor, getCustomerColor, getProductColor, getTransactionColor, getUserColor, getCategoryColor, getTypeColor, getSegmentColor } from 'constants/TagColor'
import utils from 'utils'
import axios from 'axios'
import { API_LOCAL_BASE_URL, API_LOCAL_DATA_UPDATE_URL } from 'constants/ApiConstant'
import { TableDownload } from 'constants/TableDownload'
import { Link } from 'react-router-dom'

const { Option } = Select
const { TextArea } = Input

const transactionStatusList = [ 'Client Approved', 'Sent for recording', 'Recording received', 'IVR creation', 'Testing (Phase 1)', 'Testing (Phase 2)', 'Testing (Phase 3)', 'Campaign ready' ]

const fetchData = async () => await axios({
	method: 'get',
	url: 'https://68ef17ae206a41b485fa164d62a92012.us-east-1.aws.found.io:9243/sc_transaction_demo_v2/_search?size=10000',
	auth: {
		username: 'sp_es_write',
		password: 'JxpZzs8hRV4X7'
	}
})

const changeStatus = () => {
	message.success('Status has been changed')
}

export default function SmartVoice() {

	const [ selectedRows, setSelectedRows ] = useState([])
	const [ selectedRowKeys, setSelectedRowKeys ] = useState([])
	const [ esData, setEsData ] = useState([])
	const [ list, setList ] = useState([])
	const [ editVisible, setEditVisible ] = useState(false)
	const [ editedLevel, setEditedLevel ] = useState('')
	const [ editedTranslatedContent, setEditedTranslatedContent ] = useState('')
	const [ editedRecordingLink, setEditedRecordingLink ] = useState('')
	const [ editedTransactionStatus, setEditedTransactionStatus ] = useState('')
	const [ docId, setDocId ] = useState('')
	const [ searchText, setSearchText ] = useState('')
	const [ searchedColumn, setSearchedColumn ] = useState('')
	const emptyArray = []
	let searchInput

	useEffect( () => {
		fetchData()
			.then( res => { 
				res.data.hits.hits.map( row => emptyArray.push(row._source) )
				setEsData(emptyArray)
			})
		setList(esData)
	}, [])


	const handleShowStatus = value => {
		if (value !== 'All') {
			const key = 'transactionStatus'
			const data = utils.filterArray(esData, key, value)
			setList(data)
		} else {
			setList(esData)
		}
	}

	const onLevelChange = value => setEditedLevel(value)
	const onTranslatedContentChange = event => setEditedTranslatedContent(event.target.value)
	const onRecordingLinkChange = event => setEditedRecordingLink(event.target.value)
	const onTransactionStatusChange = value => setEditedTransactionStatus(value)

	const handleEditedDetails = async () => {
		setEditVisible(false)
		await axios({
			method: 'post',
			baseURL: API_LOCAL_BASE_URL,
			url: API_LOCAL_DATA_UPDATE_URL,
			data: {
				doc_id: docId,
				level: editedLevel,
				translated_content: editedTranslatedContent,
				recording_link: editedRecordingLink,
				status: editedTransactionStatus
			}
		})
	}

	const dropdownMenu = row => (
		<Menu>
			<Menu.Item>
				<Link onClick={() => setEditVisible(true)}>
					<PlusCircleOutlined />
					<span className="ml-2">Edit</span>
				</Link>
				<Modal
					title="Edit The Transaction"
					centered
					visible={ editVisible }
					onOk={ handleEditedDetails }
					onCancel={ () => setEditVisible(false) }
					width={ 1000 }
				>
					{ selectedRows.map( elem => (
						<>
							{ setDocId(elem.doc_id) }
							<Row gutter={ 16 }>
								<Col span={ 4 }>
									<Input value={ elem.customer_name } disabled />
								</Col>
								<Col span={ 4 }>
									<Input value={ elem.channel } disabled />
								</Col>
								<Col span={ 4 }>
									<Input value={ elem.product } disabled />
								</Col>
								<Col span={ 4 }>
									<Input value={ elem.segment } disabled />
								</Col>
								<Col span={ 8 }>
									<Input value={ elem.content_name } disabled />
								</Col>
							</Row>
							<br />
							<Row gutter={ 16 }>
								<Col span={ 7 }>
									<Input value={ elem.last_updated_on } disabled />
								</Col>
								<Col span={ 3 }>
									<Input value={ elem.last_updated_by } disabled />
								</Col>
								<Col span={ 4 }>
									<Input value={ elem.segment } disabled />
								</Col>
								<Col span={ 3 }>
									<InputNumber min={ 1 } max={ 6 } defaultValue={ elem.level } onChange={ onLevelChange } />
								</Col>
								<Col span={ 7 }>
									<TextArea defaultValue={ elem.recording_link } allowClear autoSize={ true } onChange={ onRecordingLinkChange } />
								</Col>
							</Row>
							<br />
							<Row gutter={ 16 }>
								<Col span={ 9 }>
									<TextArea defaultValue={ elem.content } autoSize disabled />
								</Col>
								<Col span={ 9 }>
									<TextArea defaultValue={ elem.translated_content } allowClear autoSize={ true } onChange={ onTranslatedContentChange } />
								</Col>
								<Col span={ 6 }>
									<Select showArrow style={{ width: '100%' }} defaultValue={ elem.status } onChange={ onTransactionStatusChange } >
										{ transactionStatusList.map( transaction => (
											<Option value={ transaction } style={{ lineHeight: '32px', width: '100%' }}>
												<Tag color={ getTransactionColor(transaction) }>{ transaction }</Tag>
											</Option>
										)) }
                    				</Select>
								</Col>
							</Row>
						</>
					)) }
				</Modal>
			</Menu.Item>
		</Menu>
	)

	const getColumnSearchProps = dataIndex => ({
		filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
		  	<div style={{ padding: 8 }}>
				<Input
					ref={ node => searchInput = node }
					placeholder={`Search ${dataIndex}`}
					value={ selectedKeys[0] }
					onChange={ e => setSelectedKeys(e.target.value ? [e.target.value] : []) }
					onPressEnter={ () => handleSearch(selectedKeys, confirm, dataIndex) }
					style={{ width: 188, marginBottom: 8, display: 'block' }}
				/>
				<Space>
					<Button
						type="primary"
						onClick={ () => handleSearch(selectedKeys, confirm, dataIndex) }
						icon={ <SearchOutlined /> }
						size="small"
						style={{ width: 90 }}
					>
						Search
					</Button>
					<Button onClick={ () => handleReset(clearFilters) } size="small" style={{ width: 90 }}>
						Reset
					</Button>
				</Space>
		  	</div>
		),
		filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
		onFilter: (value, record) =>
		  	record[dataIndex] ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase()) : '',
		onFilterDropdownVisibleChange: visible => {
		  	if (visible) {
				setTimeout(() => searchInput.select(), 100);
		  	}
		},
		render: text =>
		  	searchedColumn === dataIndex ? (
				<Highlighter
					highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
					searchWords={ [searchText] }
					autoEscape
					textToHighlight={text ? text.toString() : ''}
				/>
			) : ( text )
	});

	const handleSearch = (selectedKeys, confirm, dataIndex) => {
		confirm()
		setSearchText(selectedKeys[0])
		setSearchedColumn(dataIndex)
	};

	const handleReset = clearFilters => {
		clearFilters()
		setSearchText('')
	};


	const tableColumns = [
		{
			title: 'Customer Name',
			key: 'customer_name',
			dataIndex: 'customer_name',
			...getColumnSearchProps('customer_name'),
			render: (_, record) => (
				<div className="d-flex">
					<Tag color={ getCustomerColor(record.customer_name) } style={{ letterSpacing: '1px' }}>{ record.customer_name }</Tag>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'customer_name'),
			colSpan: 1,
			width: '5%'
        },
		{
			title: 'Content Name',
			key: 'content_name',
			dataIndex: 'content_name',
			...getColumnSearchProps('content_name'),
			render: (_, record) => (
				<div className="d-flex">
					<span style={{ fontSize: '0.75rem'}}>{ record.content_name }</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'content_name'),
			colSpan: 1,
			width: '8%'
        },
        {
			title: 'Product',
			key: 'product',
			dataIndex: 'product',
			...getColumnSearchProps('product'),
			render: (_, record) => (
				<><Tag color={ getProductColor(record.product) }>{ record.product }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'product'),
			colSpan: 1
        },
        {
			title: 'Segment',
			key: 'segment',
			dataIndex: 'segment',
			...getColumnSearchProps('segment'),
			render: (_, record) => (
				<><Tag color={ getSegmentColor(record.segment) }>{ record.segment }</Tag></>	
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'segment'),
			colSpan: 1
        },
        {
			title: 'Type',
			key: 'type',
			dataIndex: 'type',
			...getColumnSearchProps('type'),
			render: (_, record) => (
				<><Tag color={ getTypeColor(record.type) }>{ record.type }</Tag></>	
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'type'),
			colSpan: 1
        },
        {
			title: 'Level',
			key: 'level',
			dataIndex: 'level',
			...getColumnSearchProps('level'),
			render: (_, record) => (
				<><Tag color={ record.level === 1 ? 'gold' : record.level === 2 ? 'orange' : 'red' }>{ record.level }</Tag></>	
			),
			sorter: (a, b) => Number(a.level) - Number(b.level),
			colSpan: 1
        },
        {
			title: 'Category',
			key: 'category',
			dataIndex: 'category',
			...getColumnSearchProps('category'),
			render: (_, record) => (
				<><Tag color={ getCategoryColor(record.category) }>{ record.category }</Tag></>	
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'category'),
			colSpan: 1
		},
		{
			title: 'Language',
			key: 'language',
			dataIndex: 'language',
			...getColumnSearchProps('language'),
			render: (_, record) => (
				<><Tag color={ getLangColor(record.language) }>{ record.language }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'language'),
			colSpan: 1
		},
		{
			title: 'Content',
			key: 'content',
			dataIndex: 'content',
			...getColumnSearchProps('content'),
			render: (_, record) => (
				<div className="d-flex">
				<span>{ record.content }</span>
			</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'content'),
			colSpan: 1,
			width: '15%'
		},
		{
			title: 'Translated Content',
			key: 'translated_content',
			dataIndex: 'translated_content',
			...getColumnSearchProps('translated_content'),
			render: (_, record) => (
				<div className="d-flex">
				<span>{ record.translated_content }</span>
			</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'translated_content'),
			colSpan: 1,
		},
		{
			title: 'Version',
			dataIndex: 'version',
			key: 'version',
			...getColumnSearchProps('version'),
			render: (_, record) => (
				<><Tag color={ record.version === 1 ? 'geekblue' : 'success' }>{ record.version }</Tag></>
			),
			sorter: (a, b) => a.version - b.version,
			colSpan: 1
		},
        {
			title: 'Transaction Status',
			key: 'status',
			dataIndex: 'status',
			...getColumnSearchProps('transaction_status'),
			render: (_, record) => (
				<><Tag color={ getTransactionColor(record.status) } onClick={ changeStatus }>{ record.status }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'status'),
			colSpan: 1
		},
        {
			title: 'Last Updated',
			key: 'last_updated_on',
			dataIndex: 'last_updated_on',
			...getColumnSearchProps('last_updated_on'),
			render: (_, record) => (
				<span>{ record.last_updated_on.split(' GMT')[0] }</span>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'last_updated_on'),
			colSpan: 1
		},
		{
			title: 'Last Updated By',
			key: 'last_updated_by',
			dataIndex: 'last_updated_by',
			...getColumnSearchProps('last_updated_by'),
			render: (_, record) => (
				<><Tag color={ getUserColor(record.last_updated_by) }>{ record.last_updated_by }</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'last_updated_by'),
			colSpan: 1
		},
		{
			title: 'Recording Link',
			key: 'recording_link',
			dataIndex: 'recording_link',
			...getColumnSearchProps('recording_link'),
			render: (_, record) => (
				<div className="d-flex">
				<span>{ record.recording_link }</span>
			</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'recording_link'),
			colSpan: 1
		},
		{
			title: '',
			key: 'actions',
			dataIndex: 'actions',
			render: (_, elm) => (
				<div className="text-right">
					<EllipsisDropdown menu={ dropdownMenu(elm) }/>
				</div>
			),
			colSpan: 1
		}
	];

	const rowSelection = {
		onChange: (key, rows) => {
			setSelectedRows(rows)
			setSelectedRowKeys(key)
		}
	};

	const onSearch = e => {
		const value = e.currentTarget.value
		const searchArray = e.currentTarget.value ? list : esData
		const data = utils.wildCardSearch(searchArray, value)
		setList(data)
		setSelectedRowKeys([])
	}

	return (
		<Card>
			<Flex alignItems="center" justifyContent="between" mobileFlex={false}>
				<Flex className="mb-1" mobileFlex={false}>
					<div className="mr-md-3 mb-3">
						<Input placeholder="Search" prefix={<SearchOutlined />} onChange={e => onSearch(e)}/>
					</div>
					<div className="mb-3">
						<Select 
							defaultValue="All" 
							className="w-100" 
							style={{ minWidth: 180 }} 
							onChange={ handleShowStatus } 
							placeholder="Status"
						>
							<Option value="All">Transactions</Option>
							{ transactionStatusList.map(elm => <Option key={elm} value={elm}>{elm}</Option>) }
						</Select>
					</div>
				</Flex>
			</Flex>
                <Flex alignItems="center" mobileFlex={false}>
					<div className="mb-3 mr-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Filter</Button>
                    </div>
					<div className="mb-3 mr-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Sort</Button>
                    </div>
                    <div className="mb-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><CloudDownloadOutlined /></span>Download Selected</Button>
                    </div>
                </Flex>
			<div className="table-responsive">
                <Table
                    bordered
					columns={ tableColumns } 
					dataSource={ esData } 
					rowKey='content_name' 
					rowSelection={{
						selectedRowKeys: selectedRowKeys,
						type: 'checkbox',
						preserveSelectedRowKeys: true,
						...rowSelection,
                    }}
                    scroll={{ x: 2500 }}
                    pagination={{ pageSize: 10 }}
                    showQuickJumper
					responsive={ true }
					size="small"
				/>
			</div>
		</Card>
	)
}
