import React, { useState } from 'react'
import PropTypes from "prop-types";
import { Grid, Drawer } from "antd";
import utils from 'utils'
import { MenuOutlined } from '@ant-design/icons';

const { useBreakpoint } = Grid;

const SideContent = props => {
	const { sideContent, sideContentWidth = 250, border } = props
	return (
		<div className={ `side-content ${ border ? 'with-border' : '' }` } style={{ width: `${sideContentWidth}px`, fontSize: '0.80rem' }}>
			{ sideContent }
		</div>
	)
}

const SideContentMobile = props => {
	const { sideContent, visible, onSideContentClose } = props
	return (
		<Drawer
			width={ 320 }
			placement="left"
			closable={ false }
			onClose={ onSideContentClose }
			visible={ visible }
			bodyStyle={{ paddingLeft: 0, paddingRight: 0 }}
		>
			<div className="text-center mt-3">
				<Avatar size={80} src={data?.img} />
				<h3 className="mt-2 mb-0">{data?.name}</h3>
				<span className="text-muted">{data?.personalInfo.title}</span>
			</div>
			<Divider dashed />
			<div className="">
				<h6 className="text-muted text-uppercase mb-3">Account details</h6>
				<p>
					<UserOutlined />
					<span className="ml-3 text-dark">id: {data?.id}</span>
				</p>
				<p>
					<CalendarOutlined />
					<span className="ml-3 text-dark">Born in {data?.personalInfo.birthday}</span>
				</p>
			</div>
			<div className="mt-5">
				<h6 className="text-muted text-uppercase mb-3">CONTACT</h6>
				<p>
					<MobileOutlined />
					<span className="ml-3 text-dark">{data?.personalInfo.phoneNumber}</span>
				</p>
				<p>
					<MailOutlined />
					<span className="ml-3 text-dark">{data?.email? data?.email: '-'}</span>
				</p>
				<p>
					<CompassOutlined />
					<span className="ml-3 text-dark">{data?.personalInfo.location}</span>
				</p>
			</div>
			<div className="mt-5">
				<h6 className="text-muted text-uppercase mb-3">Social profiles</h6>
				<p>
					<FacebookOutlined />
					<a href="/#" className="ml-3 text-dark">{data?.personalInfo.facebook? data?.personalInfo.facebook : '-'}</a>
				</p>
				<p>
					<TwitterOutlined />
					<a href="/#" className="ml-3 text-dark">{data?.personalInfo.twitter? data?.personalInfo.twitter : '-'}</a>
				</p>
				<p>
					<InstagramOutlined />
					<a href="/#" className="ml-3 text-dark">{data?.personalInfo.instagram? data?.personalInfo.instagram : '-'}</a>
				</p>
				<p>
					<GlobalOutlined />
					<a href="/#" className="ml-3 text-dark">{data?.personalInfo.site? data?.personalInfo.site : '-'}</a>
				</p>
			</div>
		</Drawer>
	)
}

const RightSideContent = props => {
	const { sideContent, sideContentWidth = 250, border } = props
	return (
		<div className={ `side-content ${ border ? 'with-border' : '' }` } style={{ width: `${sideContentWidth}px`, fontSize: '0.80rem' }}>
			{ sideContent }
		</div>
	)
}

const RightSideContentMobile = props => {
	const { sideContent, visible, onSideContentClose } = props
	return (
		<Drawer
			width={ 320 }
			placement="left"
			closable={ false }
			onClose={ onSideContentClose }
			visible={ visible }
			bodyStyle={{ paddingLeft: 0, paddingRight: 0 }}
		>
			<div className="h-100">
				{ sideContent }
			</div>
		</Drawer>
	)
}

export const InnerAppLayout = (props) => {
	const { mainContent, pageHeader, sideContentGutter = true } = props
	const isMobile = !utils.getBreakPoint(useBreakpoint()).includes('lg')
	const [ visible, setVisible ] = useState(false);

	const close = (e) => {
		setVisible(false)
	} 

	const openSideContentMobile = () => {
		setVisible(true)
	}

	return (
		<div className="inner-app-layout">
			{ isMobile ? 
				<SideContentMobile 
					visible={ visible }
					onSideContentClose={ close }
					{ ...props }
				/> 
				: 
				<SideContent { ...props}  />
			}
			<div className={ `main-content ${ pageHeader? 'has-page-header' : '' } ${ sideContentGutter? 'gutter' : 'no-gutter' }` }>
				{ isMobile ? 
					<div className={ `font-size-lg mb-3 ${ !sideContentGutter ? 'pt-3 px-3' : '' }` }>
						<MenuOutlined onClick={ () => openSideContentMobile() }/>
					</div>
					:
					null
				}
				{ mainContent }
			</div>
			{ isMobile ? 
				<RightSideContentMobile 
					visible={ visible }
					onSideContentClose={ close }
					{ ...props }
				/> 
				: 
				<RightSideContent { ...props}  />
			}
		</div>
	)
}

InnerAppLayout.propTypes = {
	sideContent: PropTypes.node,
	mainContent: PropTypes.node,
	pageHeader: PropTypes.bool,
	sideContentWidth: PropTypes.number,
	border: PropTypes.bool,
	sideContentGutter: PropTypes.bool
};

export default InnerAppLayout
