import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { message, Typography, Form, Button, Checkbox, Row, Col, Select } from 'antd'
import { CloudUploadOutlined } from '@ant-design/icons'
import axios from 'axios'
import { FILE_UPLOAD_URL } from 'constants/ApiConstant'
import XLSX from 'xlsx'
import readXlsxFile from 'read-excel-file'
import styled from 'styled-components'

const { Title } = Typography
const { Option } = Select

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

const StyledButton = styled.button`
    background: #fff;
    color: #b71540;
    font-size: 1.5rem;
    outline: 0 none;
    border: 2px solid #b71540;
    border-radius: 10px;
    cursor: pointer;
    width: 300px;
    height: 50px;
    max-width: 300px;
    max-height: 50px; 
    &:hover {
        color: white;
        background: linear-gradient(35deg, #EA2027, #b71540);
        border: transparent;
    }
`

const FileUploadForm = props => {

    let fileReader;

    let newArr = []

    const checkedLanguages = []
    const selectedOptions = []

    const [ file, setFile ] = useState(null)
    const [ visible, setVisible ] = useState(false)
    const [ buttonVisible, setButtonVisible ] = useState(true)
    const [ checked, setChecked ] = useState(false)
    const [ createTable, setCreateTable ] = useState([])
    const [ languages, setLanguages ] = useState([])
    const [ headers, setHeaders ] = useState([])

    const classes = useStyles()

    // Create a reference to the hidden file input element
    const hiddenFileInput = React.useRef(null);

    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    }; 

    const handleClick = event => {
        hiddenFileInput.current.click()
    }

    const onChange = async (e) => {
        let tableArray = []
        setFile(e.target.files[0])
        await readXlsxFile(e.target.files[0])
        .then( function(res) {
            for (let i = 0; i < 3; i++) {
                tableArray.push(res[i])
            } 
            console.log(tableArray)
            setCreateTable(tableArray)
            setVisible(true)
            setButtonVisible(false)
        })
    }

    const uploadFile = async (file) => {

        const formData = new FormData();

        formData.append('file', file)

        let res =  await axios.post(FILE_UPLOAD_URL, formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
		})
		return res
    }

    const onSubmit = async (e) => {
        let res = await uploadFile(file);
        console.log(res.data);
        checkedLanguages = []
        selectedOptions = []
        message.success('Your file was successfully submitted and is now being processed')
	}

    const onCheck = event => {
        checkedLanguages.push(event.target.value)
        console.log(checkedLanguages)
        setLanguages(checkedLanguages)
    }

    const onSelect = event => {
        selectedOptions.push(event.value)
        console.log(selectedOptions)
    }

    const renderLanguages = () => {
        const languages = ['English', 'Hindi', 'Tamil', 'Telugu', 'Kannada', 'Marathi', 'Gujarati', 'Bengali', 'Punjabi', 'Malayalam']

        return (
            <>
                <Form.Item name="language" label="Language">
                        { languages.map( language => (
                            <Checkbox.Group className="pl-3">
                                <Col>
                                    <Checkbox value={ language } style={{ lineHeight: '32px' }} onChange={ onCheck }>{ language }</Checkbox>
                                </Col>
                            </Checkbox.Group>
                        )) }
                </Form.Item>
            </>
        )
    }

    const showTableHeaders = array => {

        newArr = array[0]

        console.log(newArr)

        const headerObj = {
            Stage: '',
            Type: '',
            Level: '',
            Product: '',
            Content: '',
            Category: '',
            Vendor: ''
        }

        return (
            <>
            { newArr.map( (num, index) => (
                <Form.Item
                    name={ num }
                    label={ num }
                    hasFeedback
                >
                    <Row>
                        <Select key={ index } labelInValue placeholder={`Please enter ${ num }`} onSelect={ onSelect }>
                            { newArr.map( (num, index) => {
                                headerObj[num] = num
                                return <Option key={ index } value={ num }>{ num }</Option>
                            }) }
                        </Select>
                    </Row>
                </Form.Item>
            ))}
            {/* { setHeaders(headerObj) } */}
            </>
        )
    }


	return (
        <Form
            name="validate_other"
            { ...formItemLayout }
            onFinish={ onSubmit }
            textAlign="center"
            initialValues={{ rate: 3.5 }}
        >
			<Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%', marginBottom: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
            <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
			    <input type="file" onChange={ onChange } ref={ hiddenFileInput }  style={{ display: 'none' }} />
                { buttonVisible ? <StyledButton onClick={ handleClick }><CloudUploadOutlined style={{ marginRight: '2%' }} /> Upload File</StyledButton> : <Title level={3}>Please map the headings</Title>}
            </Form.Item>
            <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    <Row>
                        { visible && renderLanguages() }
                    </Row>
                    <Row>
                        { visible && showTableHeaders(createTable) }
                    </Row>
            </Form.Item>
			<Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="mt-5">
                <Button htmlType="submit" variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }}>Create Transactions</Button>
            </Form.Item>
            { console.log(languages, headers) }
		</Form>
	)

}
export default FileUploadForm
