/* eslint-disable no-unused-vars */
import React, {useState} from 'react'
import { Card, Table, Select, Input, Button, Badge, Menu, Tag, Pagination } from 'antd';
import OrderListData from 'assets/data/order-list.data.json'
import { EyeOutlined, CloudDownloadOutlined, SearchOutlined, PlusCircleOutlined } from '@ant-design/icons';
import EllipsisDropdown from 'components/shared-components/EllipsisDropdown';
import Flex from 'components/shared-components/Flex'
import NumberFormat from 'react-number-format';
import moment from 'moment'; 
import { DATE_FORMAT_DD_MM_YYYY } from 'constants/DateConstant'
import utils from 'utils'

const { Option } = Select

const getTransactionStatus = status => {
	if (status === 'Client Approved') return 'volcano'
	if (status === 'IVR') return 'ivr'
	if (status === 'Testing') return 'testing'
	if (status === 'Approved') return 'approved'
	return ''
}

const getUserStatus = status => {
    if (status === 'Administrator') return 'red'
    if (status === 'Campaign Team') return 'blue'
    if (status === 'DevOps Team') return 'cyan'
}

const itemRender = (current, type, originalElement) => {
    if (type === 'prev') {
      return <a>Previous</a>;
    }
    if (type === 'next') {
      return <a>Next</a>;
    }
    return originalElement;
}

const transactionStatusList = ['Client Approved', 'IVR', 'Testing', 'Approved']

export default function Dashboard() {

	const [list, setList] = useState(OrderListData)
	const [selectedRows, setSelectedRows] = useState([])
	const [selectedRowKeys, setSelectedRowKeys] = useState([])

	const handleShowStatus = value => {
		if (value !== 'All') {
			const key = 'transactionStatus'
			const data = utils.filterArray(OrderListData, key, value)
			setList(data)
		} else {
			setList(OrderListData)
		}
	}

	const dropdownMenu = row => (
		<Menu>
			<Menu.Item>
				<Flex alignItems="center">
					<EyeOutlined />
					<span className="ml-2">View Details</span>
				</Flex>
			</Menu.Item>
			<Menu.Item>
				<Flex alignItems="center">
					<PlusCircleOutlined />
					<span className="ml-2">Add to remark</span>
				</Flex>
			</Menu.Item>
		</Menu>
	);

	const tableColumns = [
		{
			title: 'ID',
			dataIndex: 'id'
		},
		{
			title: 'Version',
			key: 'version'
		},
		{
			title: 'Content Name',
			dataIndex: 'contentName',
			render: (_, record) => (
				<div className="d-flex">
					<span>{record.contentName}</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'contentName')
        },
        {
			title: 'Client Name',
			dataIndex: 'clientName',
			render: (_, record) => (
				<div className="d-flex">
					<span>{record.clientName}</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'clientName')
        },
        {
			title: 'Channel',
			dataIndex: 'channel',
			render: (_, record) => (
				<div className="d-flex">
					<span>{record.channel}</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'channel')
        },
        {
			title: 'Product',
			dataIndex: 'prooduct',
			render: (_, record) => (
				<div className="d-flex">
					<span>{record.product}</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'product')
        },
        {
			title: 'Segment',
			dataIndex: 'segment',
			render: (_, record) => (
				<div className="d-flex">
					<span>{record.segment}</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'segment')
        },
        {
			title: 'Stage',
			dataIndex: 'stage',
			render: (_, record) => (
				<div className="d-flex">
					<span>{record.stage}</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'stage')
        },
        {
			title: 'Type',
			dataIndex: 'type',
			render: (_, record) => (
				<div className="d-flex">
					<span>{record.type}</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'type')
        },
        {
			title: 'Level',
			dataIndex: 'level',
			render: (_, record) => (
				<div className="d-flex">
					<span>{record.level}</span>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'level')
        },
        {
			title: 'Transaction Status',
			dataIndex: 'transactionStatus',
			render: (_, record) => (
				<><Tag color={getTransactionStatus(record.transactionStatus)}>{record.transactionStatus}</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'transactionStatus')
		},
		{
			title: 'Due Date',
			dataIndex: 'dueDate',
			render: (_, record) => (
				<span>{moment.unix(record.dueDate).format(DATE_FORMAT_DD_MM_YYYY)}</span>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'dueDate')
        },
        {
			title: 'Last Updated',
			dataIndex: 'lastUpdated',
			render: (_, record) => (
				<span>{moment.unix(record.lastUpdated).format(DATE_FORMAT_DD_MM_YYYY)}</span>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'lastUpdated')
		},
		{
			title: 'Last Updated By',
			dataIndex: 'lastUpdatedBy',
			render: (_, record) => (
				<><Tag color={getUserStatus(record.lastUpdatedBy)}>{record.lastUpdatedBy}</Tag></>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'lastUpdatedBy')
		},
		{
			title: '',
			dataIndex: 'actions',
			render: (_, elm) => (
				<div className="text-right">
					<EllipsisDropdown menu={dropdownMenu(elm)}/>
				</div>
			)
		}
	];

	const rowSelection = {
		onChange: (key, rows) => {
			setSelectedRows(rows)
			setSelectedRowKeys(key)
		}
	};

	const onSearch = e => {
		const value = e.currentTarget.value
		const searchArray = e.currentTarget.value? list : OrderListData
		const data = utils.wildCardSearch(searchArray, value)
		setList(data)
		setSelectedRowKeys([])
	}

	return (
		<Card>
			<Flex alignItems="center" justifyContent="between" mobileFlex={false}>
				<Flex className="mb-1" mobileFlex={false}>
					<div className="mr-md-3 mb-3">
						<Input placeholder="Search" prefix={<SearchOutlined />} onChange={e => onSearch(e)}/>
					</div>
					<div className="mb-3">
						<Select 
							defaultValue="All" 
							className="w-100" 
							style={{ minWidth: 180 }} 
							onChange={ handleShowStatus } 
							placeholder="Status"
						>
							<Option value="All">Transactions </Option>
							{transactionStatusList.map(elm => <Option key={elm} value={elm}>{elm}</Option>)}
						</Select>
					</div>
				</Flex>
			</Flex>
                <Flex alignItems="center" mobileFlex={false}>
					<div className="mb-3 mr-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Filter</Button>
                    </div>
					<div className="mb-3 mr-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Sort</Button>
                    </div>
                    <div className="mb-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><CloudDownloadOutlined /></span>Download Selected</Button>
                    </div>
                </Flex>
			<div className="table-responsive">
                <Table 
                    bordered
					columns={tableColumns} 
					dataSource={list} 
					rowKey='id' 
					rowSelection={{
						selectedRowKeys: selectedRowKeys,
						type: 'checkbox',
						preserveSelectedRowKeys: false,
						...rowSelection,
                    }}
                    scroll={{ x: 2000 }}
                    pagination={{ pageSize: 10, pageSizeOptions: [5, 10, 15, 20] }}
                    showSizeChanger
                    showQuickJumper
                    responsive={true}
				/>
			</div>
		</Card>
	)
}
