import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { message, Typography, Form, Button, Checkbox, Row, Col } from 'antd'
import axios from 'axios'
import { FILE_UPLOAD_URL } from 'constants/ApiConstant'
import XLSX from 'xlsx'
import readXlsxFile from 'read-excel-file'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const { Title } = Typography

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

const FileUploadForm = props => {

    let fileReader;

    const [ file, setFile ] = useState(null)
    const [ visible, setVisible ] = useState(false)
    const [ checked, setChecked ] = useState(false)
    const [ createTable, setCreateTable ] = useState([])
    const checkedLanguages = []

    const classes = useStyles()

    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    };

    const onCheck = event => {
        checkedLanguages.push(event.target.value)
        let x = checkedLanguages.toString()
        console.log(x)
    }

    const renderLanguages = () => {
        const languages = ['English', 'Hindi', 'Tamil', 'Telugu', 'Kannada', 'Marathi', 'Gujarati', 'Bengali', 'Punjabi', 'Malayalam']

        return (
            <>
                { languages.map( language => (
                    <Col span={6}>
                        <Checkbox value={ language } style={{ lineHeight: '32px' }} onChange={ onCheck }>{ language }</Checkbox>
                    </Col>
                )) }
            </>
        )
    }

    const createTableFromArray = array => {

        // const tableHeaders = array[0]

        const tableFirstTwoRows = array.slice(1)

        const showTableHeaders = () => {
            return (
                <>
                    { array[0].map( header => (
                        <Col span={6}>
                            <Checkbox value={ header } style={{ lineHeight: '32px' }} onChange={ onCheck }>{ header }</Checkbox>
                        </Col>
                    )) }
                </>
            )
        }

        const showTableFirstTwoRows = () => {
            return (
                <>
                    { tableFirstTwoRows.map( row => (
                        <Col span={6}>
                            <Checkbox value={ row } style={{ lineHeight: '32px' }} onChange={ onCheck }>{ row }</Checkbox>
                        </Col>
                    )) }
                </>
            )
        }

        showTableHeaders()
        showTableFirstTwoRows()

        // return (
        //     <TableContainer component={ Paper }>
        //         <Table className={ classes.table } aria-label="simple table">
        //             <TableHead>
        //                 <TableRow>
        //                     { array.map( head => <TableCell>{ head }</TableCell> )}
        //                 </TableRow>
        //             </TableHead>
        //             <TableBody>
        //                 { array.map( row => <TableCell>{ row }</TableCell> )}
        //             </TableBody>
        //         </Table>
        //     </TableContainer>
        // )
    }


    const onSubmit = async (e) => {
        let res = await uploadFile(file);
        console.log(res.data);
	}

    const onChange = e => {
        let tableArray = []
        setFile(e.target.files[0])
        readXlsxFile(e.target.files[0])
        .then( function(res) {
            for (let i = 0; i < 3; i++) {
                tableArray.push(res[i])
            } 
            console.log(tableArray)
            setCreateTable(tableArray)
        })
    }

    const uploadFile = async (file) => {

        const formData = new FormData();

        formData.append('file', file)

        let res =  await axios.post(FILE_UPLOAD_URL, formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
		})
		// res.then( () => message.success(`Your ${file.name} was uploaded successfully.`) )
		// .catch( err => message.error(`A ${err.status} error occured. Please try again.`) )

		// console.log(res)
		return res
	}

	return (
        <Form
            name="validate_other"
            {...formItemLayout}
            onFinish={ onSubmit }
            textAlign="center"
            initialValues={{ rate: 3.5 }}
        >
			<Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
			<input type="file" onChange={ onChange } />
            {/* <input type="file" id="file" className="input-file" accept=".csv, .xlsx" onChange={ e => handleFileChosen(e.target.files[0])} /> */}
            <Form.Item name="language" label="Language">
                <Checkbox.Group className="pl-3">
                    <Row>
                        { renderLanguages() }
                    </Row>
                    <Row>
                        { createTableFromArray(createTable) }
                    </Row>
                </Checkbox.Group>
            </Form.Item>
			<Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="mt-5">
                <Button htmlType="submit" variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }}>Create Transactions</Button>
            </Form.Item>
		</Form>
	)

}
export default FileUploadForm
