import React from 'react'
import { Form, Select, Button, Checkbox, Row, Col, Typography, Input } from 'antd';
import { CloseCircleOutlined, PlusCircleOutlined, DownCircleOutlined } from '@ant-design/icons'
import Flex from 'components/shared-components/Flex'
const { Title } = Typography;
const { Option } = Select;
const { TextArea } = Input

const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

const FormOptions = props => {
    const onFinish = (values) => {
      console.log('Received values of form: ', values);
    };

    const renderClients = () => {
        const clients = ['AXIS Bank', 'ICICI Bank', 'RBL Bank', 'TVS', 'SBI Bank', 'IDFC Bank', 'Bajaj Auto Finance', 'TCL', 'CBD', 'ENBD', 'TCHFL', 'Kotak Mahindra', 'Avanse']

        return (
            <>
                {clients.map( client => (
                    <Option value={ client.split(' ')[0].toLowerCase() }>{ client }</Option>
                ))}    
            </>   
        )
    }

    const renderLanguages = () => {
        const languages = ['English', 'Hindi', 'Tamil', 'Telugu', 'Kannada', 'Marathi', 'Gujarati', 'Bengali', 'Punjabi', 'Malayalam']

        return (
            <>
                {languages.map( language => (
                    <Col span={6}>
                        <Checkbox value={ language } style={{ lineHeight: '32px' }}>{ language }</Checkbox>
                    </Col>
                ))}
            </>
        )
    }

    const renderProducts = () => {
        const products = ['Loan', 'Credit Card', 'MFI', 'EMI On Debit Card', 'Agri Loan', 'Tractor Loan', 'CD-SNC', 'Common Borrowers', 'Pay Later']

        return (
            <>
                {products.map( product => (
                    <Option value={ product }>{ product }</Option>
                ))}    
            </>   
        )
    }

    const renderSegments = () => {
        const segments = ['Pre-Due', 'Post-Due', 'Bucket X', 'NPA', 'W/Off', 'Phase 1', 'Phase 2', 'Phase 3', 'Phase 4 (Pre 90)', 'Phase 4 (90+)']

        return (
            <>
                {segments.map( segment => (
                    <Option value={ segment }>{ segment }</Option>
                ))}    
            </>  
        )
    }

    const renderStages = () => {
        const stages = ['Delay', 'Default']

        return (
            <>
                {stages.map( stage => (
                    <Option value={ stage }>{ stage }</Option>
                ))}    
            </>  
        )
    }

    const renderTypes = () => {
        const types = ['Reminder', 'Impact', 'Legal', 'Follow up', 'Relationship', 'Action', 'Urgency']

        return (
            <>
                {types.map( type => (
                    <Option value={ type }>{ type }</Option>
                ))}    
            </>  
        )
    }

    const renderLevels = () => {
        const levels = [1, 2, 3, 4, 5, 6]

        return (
            <>
                {levels.map( level => (
                    <Option value={ level }>{ level }</Option>
                ))}    
            </>  
        )
    }

    const renderCategories = () => {
        const categories = ['Basic', 'Detailed', 'Urgency', 'Charges', 'Clean Track', 'Good', 'CIBIL', 'Credit', 'Action', 'NC_1', 'NC_2', 'Awareness', 'Soft Reminder', 'Collection Reminder', 'Impact Reminder 1', 'Impact Reminder 2', 'Good Order', 'Long Lasting', 'Relationship Status', 'Avoid Action', 'Action To Be Initiated', 'Action Initiated', 'Degradation Of Account', 'Lgeal Inbound', 'Legal', 'Reminder', 'PTP Reminder', 'Broken PTP', 'Default', 'Delinquent', 'Legal Reminder', 'Legal Action', 'Good Order', 'Long Lasting', 'Relationship Status', 'Degradation Of Account', 'Legal To Be Initiated', 'Other', 'Reacted', 'Reaction']

        return (
            <>
                {categories.map( category => (
                    <Option value={ category }>{ category }</Option>
                ))}    
            </>  
        )
    }

    return (
        <Form
            name="validate_other"
            {...formItemLayout}
            onFinish={onFinish}
            textAlign="center"
            initialValues={{ rate: 3.5 }}
        >
            <Form.Item justify="center" style={{ textAlign: 'center' }}>
                <Title level={1}>Add a Single Transaction</Title>
            </Form.Item>
            <Form.Item style={{ margin: '0', position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
                <Flex mobileFlex={false}>
                    <div className="mb-3 mr-3">
                        <Button type="primary" onClick={() => props.history.push("/app/home")}><span style={{ marginRight: '0.5rem' }}><CloseCircleOutlined /></span>Close</Button>
                    </div>
                    <div className="mb-3 mr-3">
                        <Button type="primary" onClick={() => props.history.push('/app/massUploadTransactions')}><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Mass Upload Transactions</Button>
                    </div>
                    <div className="mb-3 mr-3">
                        <Button type="primary" onClick={() => props.history.push('/app/dropdownMenu')}><span style={{ marginRight: '0.5rem' }}><DownCircleOutlined /></span>Dropdown Menu Options</Button>
                    </div>
                </Flex>
            </Form.Item>
            <br /><br /><br />
            <Form.Item
                name="select"
                label="Client Name"
                hasFeedback
                rules={[{ required: true, message: 'Please select a client' }]}
            >
                <Select placeholder="Please select a client">
                    { renderClients() }
                </Select>
            </Form.Item>

            <Form.Item name="language" label="Language" rules={[{ required: true }]}>
                <Checkbox.Group className="pl-3">
                    <Row>
                        { renderLanguages() }
                    </Row>
                </Checkbox.Group>
            </Form.Item>

            <Form.Item
                name="product"
                label="Product"
                hasFeedback
                rules={[{ required: true, message: 'Please select a product' }]}
            >
                <Select placeholder="Please select a product">
                    { renderProducts() }
                </Select>
            </Form.Item>

            <Form.Item
                name="segment"
                label="Segment"
                hasFeedback
                rules={[{ required: true, message: 'Please select a segment' }]}
            >
                <Select placeholder="Please select a segment">
                    { renderSegments() }
                </Select>
            </Form.Item>

            <Form.Item
                name="stage"
                label="Stage"
                hasFeedback
                rules={[{ message: 'Please select a stage' }]}
            >
                <Select placeholder="Please select a stage">
                    { renderStages() }
                </Select>
            </Form.Item>

            <Form.Item
                name="type"
                label="Type"
                hasFeedback
                rules={[{ message: 'Please select a type' }]}
            >
                <Select placeholder="Please select a type">
                    { renderTypes() }
                </Select>
            </Form.Item>

            <Form.Item
                name="level"
                label="Level"
                hasFeedback
                rules={[{ message: 'Please select a level' }]}
            >
                <Select placeholder="Please select a level">
                    { renderLevels() }
                </Select>
            </Form.Item>

            <Form.Item
                name="category"
                label="Category"
                hasFeedback
                rules={[{ required: true, message: 'Please select a category' }]}
            >
                <Select placeholder="Please select a category">
                    { renderCategories() }
                </Select>
            </Form.Item>

            <Form.Item
                name="content"
                label="Content"
                rules={[{ required: true, message: 'Please enter the content' }]}
            >
                <TextArea rows={4} />
            </Form.Item>

            <Form.Item
                name="channel"
                label="Channel"
                hasFeedback
                rules={[{ required: true, message: 'Please select a channel' }]}
            >
                <Select placeholder="Please select the channel(s)">
                    <Option value="ivr">IVR</Option>
                    <Option value="sms">SMS</Option>
                    <Option value="email">E-mail</Option>
                </Select>
            </Form.Item>
            <Form.Item style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}} className="mt-5">
                <Button variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }} danger>Create and Notify Admin</Button>
            </Form.Item>
        </Form>
    )
};  

export default FormOptions
