import React from 'react'
import { InboxOutlined, CloseCircleOutlined, PlusCircleOutlined, DownCircleOutlined } from '@ant-design/icons';
import { Form, Button, Upload, Typography, Space } from 'antd';
import Flex from 'components/shared-components/Flex'
const { Text, Link, Title } = Typography;

const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

const normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
};

const MassUpload = props => {
    const onFinish = (values) => {
      console.log('Received values of form: ', values);
    };

    return (
        <Form
            name="validate_other"
            {...formItemLayout}
            onFinish={onFinish}
            initialValues={{ rate: 3.5 }}
        >
            <Form.Item justify="center" style={{ textAlign: 'center' }}>
                <Title level={1}>Mass Upload Transactions</Title>
            </Form.Item>
            <Form.Item style={{ margin: '0', position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
                <Flex mobileFlex={false}>
                    <div className="mb-3 mr-3">
                        <Button type="primary" onClick={() => props.history.push("/app/home")}><span style={{ marginRight: '0.5rem' }}><CloseCircleOutlined /></span>Close</Button>
                    </div>
                    <div className="mb-3 mr-3">
                        <Button type="primary" onClick={() => props.history.push("/app/addTransaction")}><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Transaction</Button>
                    </div>
                    <div className="mb-3 mr-3">
                        <Button type="primary" onClick={() => props.history.push('/app/dropdownMenu')}><span style={{ marginRight: '0.5rem' }}><DownCircleOutlined /></span>Dropdown Menu Options</Button>
                    </div>
                </Flex>
            </Form.Item>
            <br />
            <Form.Item justify="center" className={'mt-5 mb-5'} align="justify">
                <>
                    <Space direction="vertical">
                        <Title level={5}>
                            To mass upload transactions, follow these instructions:
                        </Title>
                        <Text>
                            1. Download the most current excel template from <Link href="#" target='_blank'>here</Link>.
                        </Text>
                        <Text>
                            2. Fill in the data in the fields as required. Make sure to select the fields that are dropdown options.
                        </Text>
                        <Text>
                            3. Upload the excel file by clicking the “Upload .XLSX File” below.
                        </Text>
                        <Text>
                            4. Map the columns to the fields in the transaction table by using the dropdown options below. For example, if Column A contains the customer names, then the “Customer Name” dropdown should be set as “Column A”.
                        </Text>
                        <Text>
                            5. Once all required fields have been mapped, click “Create & Notify Admin”
                        </Text>
                        <Text></Text>
                        <Text keyboard>
                            If you have any questions, please ask admin for help.
                        </Text>
                    </Space>
                </>
            </Form.Item>

            <Form.Item justify="center">
                <Form.Item name="excelFileUpload" valuePropName="fileList" getValueFromEvent={normFile} noStyle>
                    <Upload.Dragger name="files" action="/upload.do">
                        <p className="ant-upload-drag-icon">
                            <InboxOutlined />
                        </p>
                        <p className="ant-upload-text">Click or drag file to this area to upload</p>
                        <p className="ant-upload-hint">Support for single and bulk upload.</p>
                    </Upload.Dragger>
                </Form.Item>
            </Form.Item>
            <Form.Item style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}} className="mt-5">
                <Button variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }} danger>Create and Notify Admin</Button>
            </Form.Item>
      </Form>
    );
};  

export default MassUpload
