import React, { useState } from 'react'
import { useHistory } from 'react-router'
import { InboxOutlined } from '@ant-design/icons';
import { Form, Button, Upload, Typography, message } from 'antd';
import FileMapping from 'assets/functions/FileMapping'
import XLSX from 'xlsx'

const { Title } = Typography
const { Dragger } = Upload

const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

const ProcessExcel = data => {
    let workbook = XLSX.read(data, {
        type: 'binary'
    })
}

const normFile = event => {
    let input = event.target.files[0]
    console.log(input)
    const reader = new FileReader()
    const binaryExcel = reader.readAsBinaryString(input)
    console.log(ProcessExcel(binaryExcel))
};

const UploadTransactions = props => {

    const { push } = useHistory()

    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    }

    // --- Reading and parsing Excel File --- //

    let fileReader;

    const handleFileRead = e => {
        // const workbook = fileReader.result
        // console.log(workbook)

        const bstr = e.target.result
        const workbook = XLSX.read(bstr, )

        // let first_worksheet = workbook.Sheets[workbook.SheetNames[0]];
        // let data = XLSX.utils.sheet_to_json(first_worksheet, {header:1});

        // console.log(data)
    }

    const handleFileChosen = file => {
        let reader = new FileReader()


        reader.onloadend = handleFileRead
    }

    // -------------------------------------- //

    return (
        <Form
            name="validate_other"
            {...formItemLayout}
            onFinish={onFinish}
            initialValues={{ rate: 3.5 }}
        >
            <Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
            <Form.Item justify="center">
                {/* <input type="file" accept=".csv, .xlsx" onChange={ e => handleFileChosen(e.target.files[0]) } /> */}
                <Form.Item name="fileUpload" valuePropName="fileList" getValueFromEvent={ normFile } noStyle>
                    <Dragger>
                        <p className="ant-upload-drag-icon">
                            <InboxOutlined />
                        </p>
                        <p className="ant-upload-text">Click or drag file to this area to upload</p>
                        <p className="ant-upload-hint">Support for single and bulk upload.</p>
                    </Dragger>
                </Form.Item>
            </Form.Item>
            <Form.Item style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}} className="mt-5">
                <Button onClick={ () => push("app/journeys/dashboard")} variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }}>Create Transactions</Button>
            </Form.Item>
      </Form>
    );
};  

export default UploadTransactions
