import React, { useState } from "react";
import { Row, Col, Card, Avatar, Dropdown, Table, Menu, Tag, Badge, Typography } from 'antd';
import StatisticWidget from 'components/shared-components/StatisticWidget';
import BasicBar from 'components/shared-components/BasicBar'
import DonutChartWidget from 'components/shared-components/DonutChartWidget'
import utils from 'utils'
import { AnnualStatisticData, RecentTransactionData } from './DefaultDashboardData';
import {  sessionData, sessionLabels, combinedSessionData, sessionColor, recentOrderData } from './SalesDashboardData.js'

import { FileExcelOutlined, PrinterOutlined, EllipsisOutlined, ReloadOutlined } from '@ant-design/icons';
import { withRouter } from 'react-router-dom';

const { Title } = Typography


const IVRChart = () => (
	<DonutChartWidget 
		series={sessionData} 
		labels={sessionLabels} 
		customOptions={{colors: sessionColor}}
	/>
)

const SMSChart = () => (
	<BasicBar
		series={sessionData} 
		labels={sessionLabels} 
		customOptions={{colors: sessionColor}}
	/>
)

const switchColor = color => {
	switch (color) {
		case 'Campaign Ready': return 'green'
		case 'Testing (Phase 3)': return 'green'
		case 'Testing (Phase 2)': return '#2ecc71'
		case 'Testing (Phase 1)': return '#C4E538'
		case 'IVR creation': return '#f1c40f'
		case 'Recording receieved': return '#f39c12'
		case 'Sent for recording' : return '#e67e22'
		default: return 'red'
	}
}

const latestTransactionOption = (
	<Menu>
	  	<Menu.Item key="0">
			<span>
				<div className="d-flex align-items-center">
					<ReloadOutlined />
					<span className="ml-2">Refresh</span>
				</div>
			</span>
	  	</Menu.Item>
	  	<Menu.Item key="1">
			<span>
				<div className="d-flex align-items-center">
					<PrinterOutlined />
					<span className="ml-2">Print</span>
				</div>
			</span>
	  	</Menu.Item>
	  	<Menu.Item key="12">
			<span>
				<div className="d-flex align-items-center">
					<FileExcelOutlined />
					<span className="ml-2">Export</span>
				</div>
			</span>
	  	</Menu.Item>
	</Menu>
);
  
const cardDropdown = (menu) => (
	<Dropdown overlay={menu} trigger={['click']} placement="bottomRight">
	  <a href="/#" className="text-gray font-size-lg" onClick={e => e.preventDefault()}>
		<EllipsisOutlined />
	  </a>
	</Dropdown>
)
  
const tableColumns_1 = [
	{
	  	title: 'Customer',
	  	dataIndex: 'name',
	  	key: 'name',
	  	render: (text, record) => (
			<div className="d-flex align-items-center">
		  		<span className="ml-2">{text}</span>
			</div>
	  	),
	},
	{
	  	title: 'Date',
	  	dataIndex: 'date',
	  	key: 'date',
	},
	{
	  	title: 'Amount',
	  	dataIndex: 'amount',
	  	key: 'amount',
	},
	{
	  	title: () => <div className="text-right">Status</div>,
	  	key: 'status',
	  	render: (_, record) => (
			<div className="text-right">
		  		<Tag className="mr-0" color={switchColor(record.status)}>{record.status}</Tag>
			</div>
	  	),
	},
];

const Dashboard = () => {
	const [annualStatisticData] = useState(AnnualStatisticData);
	const [recentTransactionData] = useState(RecentTransactionData)

	return (
		<>
			<Row gutter={16}>
				<Col xs={24} sm={24} md={24} lg={24} xl={16} xxl={16}>
					<Row gutter={16}>
						{
						annualStatisticData.map((elm, i) => (
							<Col xs={24} sm={24} md={24} lg={24} xl={6} key={i}>
								<StatisticWidget 
									title={elm.title} 
									value={elm.value}
									status={elm.status}
									subtitle={elm.subtitle}
								/>
							</Col>
						))
						}
					</Row>
					<Row gutter={16}>
						<Col xs={24} sm={24} md={24} lg={24}>
							<Card title="Latest Transactions" extra={cardDropdown(latestTransactionOption)}>
								<Table 
									className="no-border-last" 
									columns={tableColumns_1} 
									dataSource={recentTransactionData} 
									rowKey='id' 
									pagination={false}
								/>
							</Card>
						</Col>
					</Row>
				</Col>
				<Col xs={24} sm={24} md={24} lg={8}>
					<IVRChart />
					<Card>
						<Title level={3} style={{ textAlign: 'center', paddingBottom: 0, marginBottom: 0 }}>SMS Progress</Title>
						<SMSChart style={{ marginBottom: 0, paddingBottom: 0 }} />
					</Card>
				</Col>
			</Row>
		</>
	)
}

export default withRouter(Dashboard)
