import React, { useState } from 'react'
import { useHistory } from 'react-router'
import { InboxOutlined } from '@ant-design/icons';
import { Form, Button, Upload, Typography, message } from 'antd';
import FileMapping from 'assets/functions/FileMapping'

const { Title } = Typography
const { Dragger } = Upload

const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

const dragProps = {
    name: 'file',
    multiple: true,
    onChange(info) {
        const status = info.file.status;
        if (status !== 'uploading') {
            console.log(info.file, info.fileList);
        }
        if (status === 'done') {
            message.success(`${info.file.name} file uploaded successfully.`);
            // FileMapping(info.file)
        } else if (status === 'error') {
            console.log(`${info.file.name} file upload failed.`);
        } 
    },
};

const normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
};

function getBase64(file, callback) {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result));
    reader.readAsArrayBuffer(file);
}

const beforeUpload = file => {
    const isSpreadsheet = file.type === 'text/csv' || 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet';
    if (!isSpreadsheet) {
      message.error('You can only upload .CSV or .XLSX  files!');
    }
    return isSpreadsheet
}

const UploadTransactions = props => {

    const { push } = useHistory()

    const [ loading, setLoading ] = useState(false)

    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    };

    const handleChange = (info) => {
        if (info.file.status === 'uploading') {
            setLoading(true);
            return;
        }
        if (info.file.status === 'done') {
            // Get this url from response in real world.
            getBase64(info.file.originFileObj, imageUrl => setLoading({ imageUrl, loading: false }));
        }
    }

    return (
        <Form
            name="validate_other"
            {...formItemLayout}
            onFinish={onFinish}
            initialValues={{ rate: 3.5 }}
        >
            <Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
            <Form.Item justify="center">
                {/* <Form.Item name="fileUpload" valuePropName="fileList" getValueFromEvent={ normFile } noStyle>
                    <Dragger>
                        <p className="ant-upload-drag-icon">
                            <InboxOutlined />
                        </p>
                        <p className="ant-upload-text">Click or drag file to this area to upload</p>
                        <p className="ant-upload-hint">Support for single and bulk upload.</p>
                    </Dragger>
                </Form.Item> */}
            </Form.Item>
            <Form.Item style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}} className="mt-5">
                <Button onClick={ () => push("app/journeys/dashboard")} variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }}>Create Transactions</Button>
            </Form.Item>
      </Form>
    );
};  

export default UploadTransactions
