import React from 'react'
import { Card, Table, Form, Select, Button, Checkbox, Row, Col, Typography, Input } from 'antd';
import { CloseCircleOutlined, PlusCircleOutlined, DownCircleOutlined } from '@ant-design/icons'
import Flex from 'components/shared-components/Flex'
const { Title } = Typography;
const { Option } = Select;
const { TextArea } = Input

function campaignAssigment() {
    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    };

    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    };

    const renderClients = () => {
        const clients = ['AXIS Bank', 'ICICI Bank', 'RBL Bank', 'TVS', 'SBI Bank', 'IDFC Bank', 'Bajaj Auto Finance', 'TCL', 'CBD', 'ENBD', 'TCHFL', 'Kotak Mahindra', 'Avanse']

        return (
            <>
                {clients.map( client => (
                    <Option value={ client.split(' ')[0].toLowerCase() }>{ client }</Option>
                ))}    
            </>   
        )
    }

    return (
        <Card>
            <Title style={{ textAlign: 'center', fontSize: '2rem', marginTop: '2%' }}>Campaign Assignment</Title>
            <Form
                name="validate_other"
                {...formItemLayout}
                onFinish={onFinish}
                textAlign="center"
                initialValues={{ rate: 3.5 }}
            >
                <Form.Item style={{ margin: '0', position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
                    <Flex mobileFlex={false}>
                        <div className="mb-3 mr-3">
                            <Button type="primary" onClick={() => props.history.push("/app/home")}><span style={{ marginRight: '0.5rem' }}><CloseCircleOutlined /></span>Close</Button>
                        </div>
                    </Flex>
                </Form.Item>
                <Form.Item
                    name="select"
                    label="Client Name"
                    hasFeedback
                    rules={[{ required: true, message: 'Please select a client' }]}
                >
                    <Select placeholder="Please select a client">
                        { renderClients() }
                    </Select>
                </Form.Item>
            </Form>
        </Card>
    )
}

export default campaignAssigment
