import React, { useState } from 'react';
import {  Card, Table, Tag, Tooltip, message, Popconfirm, Button, InputNumber, Input, Form } from 'antd';
import { EyeOutlined, DeleteOutlined } from '@ant-design/icons';
import moment from 'moment';
import UserView from './UserView';
import AvatarStatus from 'components/shared-components/AvatarStatus';
import userData from "assets/data/user-list.data.json";


const EditableCell = ({ editing, dataIndex, title, inputType, record, index, children, ...restProps }) => {
	const inputNode = inputType === 'number' ? <InputNumber /> : <Input />;
	return (
		<td { ...restProps }>
			{editing ? (
				<Form.Item
					name={ dataIndex }
					style={{ margin: 0, }}
					rules={[ { required: true, message: `Please Input ${title}!` } ]}
				>
					{ inputNode }
				</Form.Item>
			) : ( children )}
		</td>
	);
};

const UserRoles = () => {

	const [ users, setUsers ] = useState(userData)
	const [ userProfileVisible, setUserProfileVisible ] = useState(false)
	const [ selectedUser, setSelectedUser ] = useState()

	const [form] = Form.useForm();
	const [data, setData] = useState(userData);
	const [editingKey, setEditingKey] = useState('');

	const deleteUser = userId => {
		setUsers( prevUsers => {
			prevUsers.filter(item => item.id !== userId)
		})
		// this.setState({
		// 	users: this.state.users.filter(item => item.id !== userId),
		// })
		message.success({ content: `Deleted user ${userId}`, duration: 2 });
	}

	const showUserProfile = userInfo => {
		setUserProfileVisible(true)
		setSelectedUser(userInfo)
	};

	const isEditing = (record) => record.key === editingKey;

	const edit = (record) => {
		form.setFieldsValue({
			name: '',
			age: '',
			address: '',
			...record
		});
		setEditingKey(record.key);
	};

	const cancel = () => {
		setEditingKey('');
	};

	const save = async (key) => {
		try {
			const row = await form.validateFields();
			const newData = [...data];
			const index = newData.findIndex((item) => key === item.key);

			if (index > -1) {
				const item = newData[index];
				newData.splice(index, 1, { ...item, ...row });
				setData(newData);
				setEditingKey('');
			} else {
				newData.push(row);
				setData(newData);
				setEditingKey('');
			}

		} catch (errInfo) {
			console.log('Validation Failed:', errInfo);
		}
	};

	const columns = [
		{
			title: 'User',
			dataIndex: 'name',
			render: (_, record) => (
				<div className="d-flex">
					<AvatarStatus src={record.img} name={record.name} subTitle={record.email}/>
				</div>
			),
			sorter: {
				compare: (a, b) => {
					a = a.name.toLowerCase();
					b = b.name.toLowerCase();
					return a > b ? -1 : b > a ? 1 : 0;
				},
			}
		},
		{
			title: 'Role',
			dataIndex: 'role',
			render: role => (
				<Tag className ="text-capitalize" color={role === 'Admin' ? 'red' : role === 'Manager' ? 'blue' : 'cyan' }>{role}</Tag>
			),
			sorter: {
				compare: (a, b) => a.role.length - b.role.length,
			}
		},
		{
			title: 'Last online',
			dataIndex: 'lastOnline',
			render: date => (
				<Tag color={'magenta'}>{moment.unix(date).format("MM/DD/YYYY")}</Tag>
			),
			sorter: (a, b) => moment(a.lastOnline).unix() - moment(b.lastOnline).unix()
		},
		{
			title: 'Address',
			dataIndex: 'address',
			width: '30%',
			editable: true,
		},
		{
			title: 'Edit',
			dataIndex: 'edit',
			render: (_, record) => {
				const editable = isEditing(record);
				return editable ? (
				<span>
					<a
						href="javascript:;"
						onClick={() => save(record.key)}
						style={{ marginLeft: 8, marginRight: 8 }}
					>
						Save
					</a>
					<Popconfirm title="Sure to cancel?" onConfirm={cancel}>
						<a>Cancel</a>
					</Popconfirm>
				</span>
				) : (
					<a disabled={editingKey !== ''} onClick={ () => edit(record) }>
						Edit
					</a>
				);
			}
		},
		{
			title: '',
			dataIndex: 'actions',
			render: (_, elm) => (
				<div className="text-right">
					<Tooltip title="View">
						<Button type="primary" className="mr-2" icon={ <EyeOutlined /> } onClick={ showUserProfile(elm) } size="small"/>
					</Tooltip>
					<Tooltip title="Delete">
						<Button danger icon={ <DeleteOutlined /> } onClick={ elm => setUsers( userInfo => { userInfo.filter(item => item.id !== elm.id) })} size="small"/>
					</Tooltip>
				</div>
			)
		}
	];

	const mergedColumns = columns.map((col) => {
		if (!col.editable) return col

		return {
			...col,
			onCell: (record) => ({
				record,
				inputType: col.dataIndex === 'age' ? 'number' : 'text',
				dataIndex: col.dataIndex,
				title: col.title,
				editing: isEditing(record),
			}),
		};
	});

	return (
		<Card bodyStyle={{ 'padding': '0px' }}>
			<Form form={form} component={false}>
				<Table
					components={{
						body: {
							cell: EditableCell,
						}
					}}
					bordered
					dataSource={ data }
					columns={ mergedColumns }
					rowClassName="editable-row"
					pagination={{ onChange: cancel }}
				/>
			</Form>
			<UserView 
				data={ selectedUser } 
				visible={ userProfileVisible } 
				close={ () => { setUserProfileVisible(false); setSelectedUser() } }
			/>
		</Card>
	);
};

export default UserRoles
