import React, { useState, useRef } from 'react';
import { Card, Collapse, Typography, Button, Tooltip, Form, Divider, Row, Col } from 'antd'
import { makeStyles } from '@material-ui/core/styles';
import { TextField, Grid, Input } from '@material-ui/core'
import { SaveOutlined } from '@ant-design/icons'
import SearchInput from './NavSearch/SearchInput.js'
import { v4 as uuidv4 } from 'uuid';
import ExpList from './ExpList'
import RespList from './RespList'

const { Title } = Typography
const { Panel } = Collapse

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        flexGrow: 1
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: '25ch',
    },
}));

function callback(key) {
    console.log(key);
}

export default function DialogFlow(props) {

    const classes = useStyles();

    const [searchActive, setSearchActive] = useState(false)
    const [ exps, setExps ] = useState([])
    const [ resps, setResps ] = useState([])

    const expNameRef = useRef()
    const respNameRef = useRef()

    const handleAddExp = e => {
        const name = expNameRef.current.value
        if ( name === '' ) return
        setExps( prevExps => {
            return [...prevExps, { id: uuidv4(), name: name, complete: false }]
        })
        expNameRef.current.value = null
    }

    const handleAddResp = e => {
        const name = respNameRef.current.value
        if ( name === '' ) return
        setResps( prevResps => {
            return [...prevResps, { id: uuidv4(), name: name, complete: false }]
        })
        respNameRef.current.value = null
    }

    const onSearchActive = () => {
        setSearchActive(true)
    }

    const onSearchClose = () => {
        setSearchActive(false)
    }

    return (
        <Card>
            <Title style={{ fontSize: '2.5rem', textAlign: 'center', marginBottom: '2%' }}>Configure Your Journey</Title>
            <form className={classes.root} noValidate autoComplete="off">
                <Grid item xs={11}>
                    <TextField id="intent" label="Intent" variant="outlined" required style={{ width: '100%', fontSize: '2rem' }} />
                </Grid>
                <Grid item xs={1}>
                    <Button type="primary" icon={ <SaveOutlined style={{ fontSize: '1.75rem' }}/> } style={{ marginLeft: '10px', height: '100%', width: '100%', fontSize: '1rem' }} />
                </Grid>
                <Divider />
                <Grid item xs={12}>
                    <Collapse defaultActiveKey={['1']} onChange={callback} ghost>
                        <Panel header="Training Phrases" key="1" style={{ fontSize: '1rem' }}>
                            <Form.Item key="1" style={{ cursor: 'auto', width: '80%', marginLeft: '10%' }}>
                                <SearchInput />
                            </Form.Item>                  
                            <Form.Item>
                                <Grid container spacing={1} style={{ margin: '2% 0 0 10%' }} >
                                    <TextField variant="outlined" placeholder="Add user expression" inputRef={ expNameRef } style={{ width: '70%' }} />
                                    <Button type="primary" onClick={ handleAddExp } style={{ marginTop: '0.5%', marginLeft: '1%', width: '8.5%' }}>Add</Button>
                                </Grid>
                            </Form.Item>
                            <Form.Item key="1">
                                <ExpList exps={ exps } />
                            </Form.Item>  
                        </Panel>
                    </Collapse>
                </Grid>
                <Grid item xs={12} style={{ marginTop: '1%' }}>
                    <Collapse onChange={callback} ghost>
                        <Panel header="Responses" key="2" style={{ fontSize: '1rem' }}>
                            <Form.Item>
                                <Grid container spacing={1} style={{ margin: '2% 0 0 10%' }} >
                                    <TextField variant="outlined" placeholder="Enter a response" inputRef={ respNameRef } style={{ width: '70%' }} />
                                    <Button type="primary" onClick={ handleAddResp } style={{ marginTop: '0.5%', marginLeft: '1%', width: '8.5%' }}>Add</Button>
                                </Grid>
                            </Form.Item>
                            <Form.Item key="1" style={{ width: '80%', marginLeft: '10%' }}>
                                <RespList resps={ resps } />
                            </Form.Item> 
                        </Panel>
                    </Collapse>
                </Grid>
            </form>
        </Card>
    );
}
