import React, { useState, useRef } from 'react';
import { Card, Collapse, Typography, Button, Tooltip, Form, Divider, Row, Col } from 'antd'
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import { SaveOutlined } from '@ant-design/icons'
import SearchInput from './NavSearch/SearchInput.js'
import AddExp from './AddExp'
import { v4 as uuidv4 } from 'uuid';
import ExpList from './ExpList'

const { Title } = Typography
const { Panel } = Collapse

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        flexGrow: 1
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: '25ch',
    },
}));

function callback(key) {
    console.log(key);
}

export default function DialogFlow(props) {
    const classes = useStyles();
    const [searchActive, setSearchActive] = useState(false)

    const onSearchActive = () => {
        setSearchActive(true)
    }

    const onSearchClose = () => {
        setSearchActive(false)
    }

    // const [ todos, setTodos ] = useState([])
    const [ exps, setExps ] = useState([])

    //const todoNameRef = useRef()
    const expNameRef = useRef()

    // function handleAddTodo(event) {
    //     const name = todoNameRef.current.value
    //     if ( name === '' ) return
    //     setTodos( prevTodos => {
    //         return [...prevTodos, { id: uuidv4(), name: name, complete: false }]
    //     })
    //     todoNameRef.current.value = null
    // }

    const handleAddExp = e => {
        const name = expNameRef.current.value
        if ( name === '' ) return
        setExps( prevExps => {
            return [...prevExps, { id: uuidv4(), name: name, complete: false }]
        })
        expNameRef.current.value = null
    }

    return (
        <Card>
            <Title style={{ fontSize: '2.5rem', textAlign: 'center', marginBottom: '2%' }}>Configure Your Journey</Title>
            <form className={classes.root} noValidate autoComplete="off">
                <Grid item xs={11}>
                    <TextField id="intent" label="Intent" variant="outlined" required style={{ width: '100%', fontSize: '2rem' }} />
                </Grid>
                <Grid item xs={1}>
                    <Button type="primary" icon={ <SaveOutlined style={{ fontSize: '1.75rem' }}/> } style={{ marginLeft: '10px', height: '100%', width: '100%', fontSize: '1rem' }} />
                </Grid>
                <Divider />
                <Grid item xs={12}>
                    <Collapse defaultActiveKey={['1']} onChange={callback} ghost>
                        <Panel header="Training Phrases" key="1" style={{ fontSize: '1rem' }}>
                            <Form.Item key="1" style={{ cursor: 'auto' }}>
                                <SearchInput />
                            </Form.Item>
                            <Form.Item>
                                <Grid container spacing={1} alignItems="flex-end">
                                    <Row>
                                        <ExpList exps={ exps } />
                                    </Row>
                                    <Row>
                                        <Col span={20}>
                                            <TextField id="input-with-icon-grid" label="Add user expression" ref={ expNameRef } />
                                            <input type="text" placeholder="Add a To-Do" ref={ expNameRef } />
                                        </Col>
                                        <Col span={4}>
                                            <Button type="primary" onClick={ handleAddExp }>Add</Button>
                                        </Col>
                                    </Row>
                                </Grid>
                            </Form.Item>
                        </Panel>
                    </Collapse>
                </Grid>
                <Grid item xs={12} style={{ marginTop: '1%' }}>
                    <Collapse onChange={callback} ghost>
                        <Panel header="Responses" key="2" style={{ fontSize: '1rem' }}>
                            <Form.Item>
                                <AddExp />
                            </Form.Item>
                        </Panel>
                    </Collapse>
                </Grid>
            </form>
        </Card>
    );
}
