import React, { Component } from 'react'
import Chart from "react-apexcharts";
import { COLORS } from 'constants/ChartConstant';

export class BasicBar extends Component {
	state = {
		series: [
			{
				name: 'Client Approved',
				data: 750
			},
			{
				name: 'Sent for translation',
				data: 450
			},
			{
				name: 'SMS received',
				data: 392
			},
			{
				name: 'DLT approval',
				data: 50
			},
			{
				name: 'Testing (Phase 1)',
				data: 215
			},
			{
				name: 'Testing (Phase 2)',
				data: 150
			},
			{
				name: 'Testing (Phase 3)',
				data: 105
			},
			{
				name: 'Campaign ready',
				data: 95
			},

		],
		options: {
			plotOptions: {
				bar: {
					horizontal: true,
				}
			},
			stroke: {
			  	width: 1,
			  	colors: ['#fff']
			},
			chart: {
				type: 'bar',
				height: 500,
				toolbar: {
				  show: true,
				  tools: {
					download: false
				  }
				}
			},
			colors: COLORS,
			dataLabels: {
				enabled: false
			},
			xaxis: {
				categories: [ 'Today' ],
				labels: {
					formatter: function(val) {
						return val
					}
				}
			},
			tooltip: {
				y: {
				  formatter: function (val) {
					return val
				  }
				}
			},
			fill: {
				opacity: 1
			},
		}
	};

	render() {
		return (
			<Chart
				options={this.state.options}
				series={this.state.series}
				type="bar"
				height= {215}
				style={{ paddingTop: 0, marginTop: 0 }}
			/>
		)
	}
}

export default BasicBar
