import React, { useEffect, useState } from 'react'
import { Card, Tag, Table, Form, Select, Button, Checkbox, Row, Col, Typography, Input } from 'antd';
import { CloseCircleOutlined, PlusCircleOutlined, DownCircleOutlined, SearchOutlined } from '@ant-design/icons'
import Flex from 'components/shared-components/Flex'
import ConditionalElem from './ConditionalElem'
import AvatarStatus from 'components/shared-components/AvatarStatus';
import agentData from "assets/data/agent-list.data.json";
import { DataGrid } from '@material-ui/data-grid'
import utils from 'utils'

const { Title } = Typography;
const { Option } = Select;
const { TextArea } = Input

const AGENT_ASSIGNED_API_URL = 'http://dev2.spoctocular.com/test_api/public/api/agent_assignedlist'
const CAMPAIGNS_API_URL = 'http://dev2.spoctocular.com/test_api/public/api/campaigns'
const AGENT_DETAILS_API_URL = 'http://dev2.spoctocular.com/test_api/public/api/agentdetails'

function CampaignAssigment(props) {

    const[ camps, setCamps ] = useState([])
    const[ agents, setAgents ] = useState([])
    const[ details, setDetails ] = useState([])
    const [ objLabel, setLabel ] = useState()
    const [ visible, setVisible ] = useState(false)
    const [checkStrictly, setCheckStrictly] = useState(false)
    const [selectedRows, setSelectedRows] = useState([])
    const [selectedRowKeys, setSelectedRowKeys] = useState([])
    const [list, setList] = useState(details)

    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    };

    const columns = [
		{
            title: 'User',
            key: 'name',
			dataIndex: 'name',
			render: (_, record) => (
				<div className="d-flex">
					<AvatarStatus  key={record.id} src={record.img} name={record.name} subTitle={record.name.toLowerCase() + "@spocto.com"}/>
				</div>
			),
			sorter: {
				compare: (a, b) => {
					a = a.name.toLowerCase();
					b = b.name.toLowerCase();
					return a > b ? -1 : b > a ? 1 : 0;
				},
			},
			editable: true
        },
        {
            title: 'Active Chats',
            key: 'active',
			dataIndex: 'activeChats',
			render: num => (
                <Tag color={'red'}>{ num }</Tag>
			),
            sorter: {
				compare: (a, b) => a.activeChats.length - b.activeChats.length,
			}
		},
    ]

    function fetchAPI_Campaigns(url) {
        fetch(url)
            .then( response => response.json() )
            .then( res => setCamps(res.campaigns))
    }

    function fetchAPI_Agents_Assigned(url) {
        fetch(url)
            .then( response => response.json() )
            .then( res => setAgents(res.agentassigned))
    }

    function fetchAPI_Agents_Details(url) {
        fetch(url)
            .then( response => response.json() )
    }

    function loadServerRows() {
        return new Promise((resolve, reject) => {
            fetch(AGENT_DETAILS_API_URL)
            .then( response => response.json() )
            .then((data) => {
                resolve({
                    rows: data.map((row) => ({ key: row.id, ...row }))
                });
            })
            .catch((error) => reject(error));
        });
      }

    useEffect(() => {
        fetchAPI_Campaigns(CAMPAIGNS_API_URL)
        fetchAPI_Agents_Assigned(AGENT_ASSIGNED_API_URL)
        const rows = loadServerRows()
        setDetails(rows)
    }, [])


    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    };

    const renderCampaignName = () => {

        return (
            <>
                {camps.map( name => (
                    <Option value={ name.id }>{ name.name }</Option>
                ))}  
            </>   
        )
    }

    // const renderCampaignId = () => {

    //     return (
    //         <>
    //             {camps.map( name => {
    //                 name.name === 'Demo - DEBT Collect' ? <Tag color="red">20</Tag> :
    //                 name.name === 'Demo - Payment Reminder' ? <Tag color="red">21</Tag> :
    //                 name.name === 'Demo - Legal Reminder' ? <Tag color="red">22</Tag> :
    //                 <Tag color="red">32</Tag>
    //             })}
    //         </>   
    //     )
    // }

    const selectCampaignOption = value => {
        console.log(`${value.label} has been selected`)
        setVisible(true)
        setLabel(value.label)
    }

    const rowSelection = {
		onChange: (key, rows) => {
			setSelectedRows(rows)
			setSelectedRowKeys(key)
		}
	};

    const onSearch = e => {
		const value = e.currentTarget.value
		const searchArray = e.currentTarget.value ? details : details
		const data = utils.wildCardSearch(searchArray, value)
		setDetails(data)
        setSelectedRowKeys([])
        setDetails(details)
    }

    const handleSubmit = () => {
        console.log('Button clicked')
    }

    return (
        <Card>
            <Title style={{ textAlign: 'center', fontSize: '2rem', marginTop: '2%', marginBottom: '4%' }}>Campaign Assignment</Title>
            <Form
                name="validate_other"
                {...formItemLayout}
                onFinish={onFinish}
                textAlign="center"
                initialValues={{ rate: 3.5 }}
            >
                <Form.Item style={{ margin: '0', position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
                    <Flex mobileFlex={false}>
                        <div className="mb-3 mr-3">
                            <Button type="primary" onClick={() => props.history.push("/app/home")}><span style={{ marginRight: '0.5rem' }}><CloseCircleOutlined /></span>Close</Button>
                        </div>
                    </Flex>
                </Form.Item>
                <Form.Item
                    name="select"
                    label="Campaign Name"
                    hasFeedback
                    rules={[{ required: true, message: 'Please select a campaign name' }]}
                >
                    <Select labelInValue placeholder="Please select a campaign name" onChange={ selectCampaignOption }>
                        { renderCampaignName() }
                    </Select>
                </Form.Item>
                { visible && 
                    <>
                        <Flex alignItems="center" justifyContent="between" mobileFlex={false}>
                            <Flex className="mb-1" mobileFlex={false}>
                                <div className="mr-md-3 mb-3" style={{ marginLeft: '5%'}}>
                                    <Input placeholder="Search" prefix={<SearchOutlined />} onChange={e => onSearch(e)}/>
                                </div>
                            </Flex>
                        </Flex>
                        {/* <Form.Item name="tag" label="Campaign ID" hasFeedback>
                            { renderCampaignId() }
                        </Form.Item> */}
                        <ConditionalElem />
                        <Table
                            dataSource={details}
                            columns={columns}
                            rowSelection
                        />
                        <Form.Item style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}} className="mt-5">
                            <Button variant="contained" type="primary" onClick={ handleSubmit } style={{ width: '100%', height: '3rem', fontSize: '1.25rem' }}>Submit</Button>
                        </Form.Item>
                    </>
                }
            </Form>
        </Card>
    )
}

export default CampaignAssigment
