export const tableColumns = [
    {
        title: 'Channel',
        dataIndex: 'channel',
        render: (_, record) => (
            <div className="d-flex">
                <Tag color={ getChannelColor(record.channel) }>{ record.channel }</Tag>
            </div>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'channel'),
        key: 'channel'
    },
    {
        title: 'Customer Name',
        dataIndex: 'customer_name',
        render: (_, record) => (
            <div className="d-flex">
                <Tag color={ getCustomerColor(record.customer_name) } style={{ letterSpacing: '1px' }}>{ record.customer_name }</Tag>
            </div>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'customer_name'),
        key: 'customer_name'
    },
    {
        title: 'Content Name',
        dataIndex: 'content_name',
        render: (_, record) => (
            <div className="d-flex">
                <span>{ record.content_name }</span>
            </div>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'content_name'),
        key: 'content_name'
    },
    {
        title: 'Product',
        dataIndex: 'product',
        render: (_, record) => (
            <><Tag color={ getProductColor(record.product) }>{ record.product }</Tag></>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'product'),
        key: 'product'
    },
    {
        title: 'Segment',
        dataIndex: 'stage',
        render: (_, record) => (
            <div className="d-flex">
                <span>{ record.segment }</span>
            </div>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'segment'),
        key: 'segment'
    },
    {
        title: 'Type',
        dataIndex: 'type',
        render: (_, record) => (
            <div className="d-flex">
                <span>{ record.type }</span>
            </div>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'type'),
        key: 'type'
    },
    {
        title: 'Level',
        dataIndex: 'level',
        render: (_, record) => (
            <div className="d-flex">
                <span>{ record.level }</span>
            </div>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'level'),
        key: 'level'
    },
    {
        title: 'Category',
        dataIndex: 'category',
        render: (_, record) => (
            <div className="d-flex">
                <span>{ record.category }</span>
            </div>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'category'),
        key: 'category'
    },
    {
        title: 'Language',
        key: 'language',
        dataIndex: 'language',
        render: (_, record) => (
            <><Tag color={ getLangColor(record.language) }>{ record.language }</Tag></>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'language'),
    },
    {
        title: 'Content',
        key: 'content',
        dataIndex: 'content',
        render: (_, record) => (
            <div className="d-flex">
            <span>{ record.content }</span>
        </div>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'language'),
    },
    {
        title: 'Version',
        dataIndex: 'version',
        key: 'version',
        render: (_, record) => (
            <><Tag color={ record.version === 1 ? 'geekblue' : 'success' }>{ record.version }</Tag></>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'language'),
    },
    {
        title: 'Transaction Status',
        dataIndex: 'status',
        key: 'status',
        render: (_, record) => (
            <><Tag color={ getTransactionStatus(record.status) } onClick={ changeStatus }>{ record.status }</Tag></>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'status')
    },
    {
        title: 'Last Updated',
        dataIndex: 'last_updated_on',
        render: (_, record) => (
            <span>{ record.last_updated_on.split(' GMT')[0] }</span>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'last_updated_on'),
        key: 'last_updated_on'
    },
    {
        title: 'Last Updated By',
        dataIndex: 'last_updated_by',
        render: (_, record) => (
            <><Tag color={ getUserStatus(record.last_updated_by) }>{ record.last_updated_by }</Tag></>
        ),
        sorter: (a, b) => utils.antdTableSorter(a, b, 'last_updated_by'),
        key: 'last_updated_by'
    },
    {
        title: '',
        dataIndex: 'actions',
        render: (_, elm) => (
            <div className="text-right">
                <EllipsisDropdown menu={dropdownMenu(elm)}/>
            </div>
        ),
        key: 'actions'
    }
];
