import React, { useState } from 'react'
import { message, Typography, Form } from 'antd'
import axios from 'axios'
import { FILE_UPLOAD_URL } from 'constants/ApiConstant'

const { Title } = Typography

const FileUploadForm = props => {
	const [ file, setFile ] = useState(null)

    const onSubmit = async (e) => {
        e.preventDefault() 
        let res = await uploadFile(file);
        console.log(res.data);
	}

    const onChange = e => {
        setFile(e.target.files[0])
	}

    const uploadFile = async (file) => {

        const formData = new FormData();

        formData.append('file', file)

        return await axios.post(FILE_UPLOAD_URL, formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
		})
		// .then( res => message.success(res) )
		// .catch( err => console.log(err) )
	}

	return (
		<form onSubmit={ onSubmit }>
			<Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
			<input type="file" onChange={ onChange } />
			<button type="submit">Upload File</button>
		</form>
	)

}
export default FileUploadForm
