import React, { useContext, useState, useEffect, useRef } from 'react';
import {  Card, Table, Tag, Tooltip, message, Popconfirm, Button, InputNumber, Input, Form } from 'antd';
import { EyeOutlined, DeleteOutlined } from '@ant-design/icons';
import moment from 'moment';
import UserView from './UserView';
import AvatarStatus from 'components/shared-components/AvatarStatus';
import userData from "assets/data/user-list.data.json";

const EditableContext = React.createContext();

const EditableRow = ({ index, ...props }) => {
  	const [form] = Form.useForm();
  	return (
		<Form form={form} component={false}>
			<EditableContext.Provider value={form}>
				<tr {...props} />
			</EditableContext.Provider>
		</Form>
  	);
};

const EditableCell = ({ title, editable, children, dataIndex, record, handleSave, ...restProps }) => {

	const [editing, setEditing] = useState(false);
	const inputRef = useRef();
	const form = useContext(EditableContext);
	useEffect(() => {
		if (editing) {
		inputRef.current.focus();
		}
	}, [editing]);

	const toggleEdit = () => {
		setEditing(!editing);
		form.setFieldsValue({
		[dataIndex]: record[dataIndex],
		});
	};

	const save = async (e) => {
		try {
		const values = await form.validateFields();
		toggleEdit();
		handleSave({ ...record, ...values });
		} catch (errInfo) {
		console.log('Save failed:', errInfo);
		}
	};

	let childNode = children;

	if (editable) {
		childNode = editing ? (
		<Form.Item
			style={{ margin: 0 }}
			name={dataIndex}
			rules={[ { required: true, message: `${title} is required.` } ]}
		>
			<Input ref={inputRef} onPressEnter={save} onBlur={save} />
		</Form.Item>
		) : (
		<div
			className="editable-cell-value-wrap"
			style={{ paddingRight: 24 }}
			onClick={toggleEdit}
		>
			{children}
		</div>
		);
	}

  	return <td {...restProps}>{childNode}</td>;
};

class EditableTable extends React.Component {
	constructor(props) {
		super(props);
    this.columns = [
		{
			title: 'User',
			dataIndex: 'name',
			render: (_, record) => (
				<div className="d-flex">
					<AvatarStatus src={record.img} name={record.name} subTitle={record.email}/>
				</div>
			),
			sorter: {
				compare: (a, b) => {
					a = a.name.toLowerCase();
					b = b.name.toLowerCase();
					return a > b ? -1 : b > a ? 1 : 0;
				},
			},
			editable: true
		},
		{
			title: 'Role',
			dataIndex: 'role',
			render: role => (
				<Tag className ="text-capitalize" color={role === 'Admin' ? 'red' : role === 'Manager' ? 'blue' : 'cyan' }>{role}</Tag>
			),
			sorter: {
				compare: (a, b) => a.role.length - b.role.length,
			}
		},
		{
			title: 'Last online',
			dataIndex: 'lastOnline',
			render: date => (
				<Tag color={'magenta'}>{moment.unix(date).format("MM/DD/YYYY")}</Tag>
			),
			sorter: (a, b) => moment(a.lastOnline).unix() - moment(b.lastOnline).unix()
		},
		{
			title: 'Operation',
			dataIndex: 'operation',
			render: (text, record) =>
			this.state.dataSource.length >= 1 ? (
				<Popconfirm title="Sure to delete?" onConfirm={() => this.handleDelete(record.key)}>
					<DeleteOutlined />
				</Popconfirm>
			) : null,
		},
	];

    this.state = {
		dataSource: userData,
		count: 2,
    };
}

  handleDelete = (key) => {
    const dataSource = [...this.state.dataSource];
    this.setState({
      dataSource: dataSource.filter((item) => item.key !== key),
    });
  };
  handleAdd = () => {
    const { count, dataSource } = this.state;
    const newData = {
      key: count,
      name: `Edward King ${count}`,
      age: 32,
      address: `London, Park Lane no. ${count}`,
    };
    this.setState({
      dataSource: [...dataSource, newData],
      count: count + 1,
    });
  };
  handleSave = (row) => {
    const newData = [...this.state.dataSource];
    const index = newData.findIndex((item) => row.key === item.key);
    const item = newData[index];
    newData.splice(index, 1, { ...item, ...row });
    this.setState({
      dataSource: newData,
    });
  };

  render() {
    const { dataSource } = this.state;
    const components = {
      body: {
        row: EditableRow,
        cell: EditableCell,
      },
    };
    const columns = this.columns.map((col) => {
      if (!col.editable) {
        return col;
      }

      return {
        ...col,
        onCell: (record) => ({
          record,
          editable: col.editable,
          dataIndex: col.dataIndex,
          title: col.title,
          handleSave: this.handleSave,
        }),
      };
    });
    return (
      <div>
        <Button
          onClick={this.handleAdd}
          type="primary"
          style={{
            marginBottom: 16,
          }}
        >
          Add a row
        </Button>
        <Table
          components={components}
          rowClassName={() => 'editable-row'}
          bordered
          dataSource={dataSource}
          columns={columns}
        />
      </div>
    );
  }
}

export default EditableTable
