import React, { useState } from 'react'
import { Card, Progress, message, Modal, Button, Tooltip, Typography, Steps } from 'antd'
import { CheckCircleOutlined, LikeOutlined, ExclamationCircleOutlined, SafetyOutlined, SendOutlined, AudioOutlined, CustomerServiceOutlined, SmileOutlined, InfoCircleOutlined, FileProtectOutlined, MessageOutlined, MailOutlined } from '@ant-design/icons';
import { render } from 'node-sass';

const { Title, Paragraph } = Typography
const { Step } = Steps;

const Dashboard = () => {

  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
    showProgress()
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const showProgress = () => {

    return (
      <Modal title="Basic Modal" visible={ isModalVisible } onOk={ handleOk } onCancel={ handleCancel }>
        <p>Some contents...</p>
        <p>Some contents...</p>
        <p>Some contents...</p>
      </Modal>
    )

  }

  return (
    <Card>
      <Card>
        <Title style={{ marginBottom: '3%', textAlign: 'center' }}>IVR Journey <span style={{ fontSize: '50%', cursor: 'pointer' }} onClick={ showModal }>{ <InfoCircleOutlined /> }</span></Title>
        <Steps labelPlacement="vertical">
          <Step status="process" description="Client Approved" icon={<ExclamationCircleOutlined style={{ color: 'red' }} />} />
          <Step status="process" description="Sent for recording" icon={<SendOutlined style={{ color: '#e67e22' }} />} />
          <Step status="process" description="Recording received" icon={<AudioOutlined style={{ color: '#f39c12' }} />} />
          <Step status="process" description="IVR creation" icon={<CustomerServiceOutlined style={{ color: '#f1c40f' }} />} />
          <Step status="process" description="Testing (Phase 1)" icon={<CheckCircleOutlined style={{ color: '#C4E538' }} />}/>
          <Step status="process" description="Testing (Phase 2)" icon={<SmileOutlined style={{ color: '#2ecc71' }} />} />
          <Step status="process" description="Testing (Phase 3)" icon={<SafetyOutlined style={{ color: 'green' }} />} />
          <Step status="process" description="Campaign ready" icon={<LikeOutlined style={{ color: 'darkgreen' }} />} />
        </Steps>
      </Card>
      <Card>
        <Title style={{ marginBottom: '3%', textAlign: 'center' }}>SMS Journey <span style={{ fontSize: '50%' }}>{ <InfoCircleOutlined /> }</span></Title>
        <Steps labelPlacement="vertical">
          <Step status="process" description="Client Approved" icon={<ExclamationCircleOutlined style={{ color: 'red' }} />} />
          <Step status="process" description="Sent for translation" icon={<SendOutlined style={{ color: '#e67e22' }} />} />
          <Step status="process" description="SMS received" icon={<MessageOutlined style={{ color: '#f39c12' }} />} />
          <Step status="process" description="DLT approval" icon={<FileProtectOutlined style={{ color: '#f1c40f' }} />} />
          <Step status="process" description="Testing (Phase 1)" icon={<CheckCircleOutlined style={{ color: '#C4E538' }} />}/>
          <Step status="process" description="Testing (Phase 2)" icon={<SmileOutlined style={{ color: '#2ecc71' }} />} />
          <Step status="process" description="Testing (Phase 3)" icon={<SafetyOutlined style={{ color: 'green' }} />} />
          <Step status="process" description="Campaign ready" icon={<LikeOutlined style={{ color: 'darkgreen' }} />} />
        </Steps>
      </Card>
      <Card>
        <Title style={{ marginBottom: '3%', textAlign: 'center' }}>E-mail Journey <span style={{ fontSize: '50%' }}>{ <InfoCircleOutlined /> }</span></Title>
        <Steps labelPlacement="vertical">
          <Step status="process" description="Client Approved" icon={<ExclamationCircleOutlined style={{ color: 'red' }} />} />
          <Step status="process" description="E-mail design" icon={<MailOutlined style={{ color: '#f1c40f' }} />} />
          <Step status="process" description="Testing (Phase 1)" icon={<CheckCircleOutlined style={{ color: '#C4E538' }} />}/>
          <Step status="process" description="Testing (Phase 2)" icon={<SmileOutlined style={{ color: '#2ecc71' }} />} />
          <Step status="process" description="Testing (Phase 3)" icon={<SafetyOutlined style={{ color: 'green' }} />} />
          <Step status="process" description="Campaign ready" icon={<LikeOutlined style={{ color: 'darkgreen' }} />} />
        </Steps>
      </Card>
    </Card>
  )
}

export default Dashboard

