import React, { useState, useRef } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles';
import { Typography, Form, Button, Checkbox, Row, Col, Card, message, Select } from 'antd'
import { CloudUploadOutlined, LeftCircleOutlined, RightCircleOutlined } from '@ant-design/icons'
import axios from 'axios'
import readXlsxFile from 'read-excel-file'
import { API_LOCAL_BASE_URL, API_LOCAL_DATA_POST_URL, } from 'constants/ApiConstant'
import { StyledButton, StyledNavButton } from 'constants/StyledComponents'
import { MONTHS } from 'constants/DateConstant'
import { APP_PREFIX_PATH } from 'configs/AppConfig'

const { Title } = Typography
const { Option } = Select

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

const FileUploadForm = props => {

    // ---------------------------- VARIABLE ASSIGNMENT ---------------------------- //

    // let fileReader;

    // let newArr = []

    let checkedLanguages = []
    // let selectedOptions = []

    const [ file, setFile ] = useState(null)
    // const [ rows, setRows ] = useState(null)
    // const [ visible, setVisible ] = useState(false)
    // const [ show, setShow ] = useState(false)
    const [ buttonVisible, setButtonVisible ] = useState(true)
    const [ checked, setChecked ] = useState(false)
    const [ createTable, setCreateTable ] = useState([])
    const [ languages, setLanguages ] = useState([])

    // Create a reference to the hidden file input element
    const hiddenFileInput = useRef(null);

    const history = useHistory()

    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    }; 

    const map = {
        'Product': 'product',
        'Segment': 'segment',
        'Type': 'type',
        'Level': 'level',
        'Category': 'category',
        'Content': 'content',
        'Vendor': 'vendor',
        'Languages': 'languages',
        'Status': 'status'
    }

    // --------------------------------------------------------------------- //
    // ------------------------ FUNCTION ASSIGNMENT ------------------------ //
    // --------------------------------------------------------------------- //

    const showButtons = () => {

        return (
            <>
                <Card>
                    <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginTop: 0, marginBottom: 0, paddingtop: 0, paddingTop: 0 }}>
                        <Row>
                            { renderLanguages() }
                        </Row>
                        <Row>
                            { renderClients() }
                        </Row>
                    </Form.Item>
                </Card>
                <StyledButton onClick={ handleClick } style={{ marginTop: '7.5%' }}><CloudUploadOutlined style={{ marginRight: '2%' }} /> Upload File</StyledButton>
                <Row style={{ marginTop: '12.5%', display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
                    <StyledNavButton style={{ marginRight: '3%'}}><LeftCircleOutlined style={{ marginRight: '2%' }} />Go to recommendations</StyledNavButton>
                    <Link to={`${APP_PREFIX_PATH}/journeys/journey-ivr/transaction`}>
                        <StyledNavButton>Go to transactions<RightCircleOutlined style={{ marginLeft: '2%' }} /></StyledNavButton>
                    </Link>
                </Row>
            </>
        )
    }

    const handleClick = () => {
        hiddenFileInput.current.click()
    }

    const onChange = async (e) => {
        e.preventDefault()
        // let tableArray = []
        let customer_name = 'ICICI Bank'
        const formData = new FormData()
        let todayDate = new Date()
        let formattedDay = `${ todayDate.getDate() > 3 && todayDate.getDate() < 21 ? `${todayDate.getDate()}th` : todayDate.getDate() in [1, 21, 31] ? `${todayDate.getDate()}st` : todayDate.getDate() in [2, 22] ? `${todayDate.getDate()}nd` : `${todayDate.getDate()}rd` }`
        let formattedTime = `${todayDate.getHours()}:${todayDate.getMinutes() < 10 ? `0${todayDate.getMinutes()}` : todayDate.getMinutes()} hrs`
        setFile(e.target.files[0])
        await readXlsxFile(e.target.files[0], { map })
        .then( async ({ rows }) => {
            rows.forEach( row => (
                row.channel = 'smartVoice',
                row.customer_name = customer_name,
                row.language = languages,
                row.status = 'Client Approved',
                row.last_updated_on = `${formattedDay} ${MONTHS[todayDate.getMonth()]}, ${todayDate.getFullYear()} at ${formattedTime}`,
                row.last_updated_by = 'Admin',
                row.version = 1
            ))
            formData.append('client_data', rows)
            props.esData(rows)
            await axios({
                method: 'post',
                baseURL: API_LOCAL_BASE_URL,
                url: API_LOCAL_DATA_POST_URL,
                data: rows
            })
            .then( res => console.log(`Connection with STATUS ${res.status} has been established.`))
            .catch( function(err) {
                if (err.response) {
                    console.log(err.response, err.response.data, err.response.status, err.response.headers)
                } else if (err.request) {
                    console.log(err.request)
                } else {
                    console.log('Error', err.message);
                }
                console.log(err.config);
            })
        })
        // .then( function(res) {
        //     for (let i = 0; i < 3; i++) {
        //         tableArray.push(res[i])
        //     } 
        //     console.log(tableArray)
        //     setCreateTable(tableArray)
        //     setVisible(true)
        //     setButtonVisible(false)
        // })
    }

    const onCheck = event => {
        checkedLanguages.push(event.target.value)
        setLanguages(prevLanguages => prevLanguages.concat(checkedLanguages))
    }

    const renderClients = () => {
        const clients = ['Axis Bank', 'ICICI Bank', 'RBL Bank', 'TVS', 'SBI Bank', 'IDFC Bank', 'Bajaj Auto Finance', 'TCL', 'CBD', 'ENBD', 'TCHFL', 'Kotak Mahindra', 'Avanse']

        return (
            <>
                <Form.Item name="client" label="client">
                        { clients.map( client => (
                            <Checkbox.Group className="pl-3 mt-1">
                                <Col>
                                    <Checkbox value={ client } style={{ lineHeight: '32px' }} onChange={ onCheck }>{ client }</Checkbox>
                                </Col>
                            </Checkbox.Group>
                        )) }
                </Form.Item>  
            </> 
        )
    }

    const renderLanguages = () => {
        const languages = ['English', 'Hindi', 'Tamil', 'Telugu', 'Kannada', 'Marathi', 'Gujarati', 'Bengali', 'Punjabi', 'Malayalam']

        return (
            <>
                <Form.Item name="language" label="Language">
                        { languages.map( language => (
                            <Checkbox.Group className="pl-3 mt-1">
                                <Col>
                                    <Checkbox value={ language } style={{ lineHeight: '32px' }} onChange={ onCheck }>{ language }</Checkbox>
                                </Col>
                            </Checkbox.Group>
                        )) }
                </Form.Item>
            </>
        )
    }


    // const showTableHeaders = array => {

    //     newArr = array[0]

    //     console.log(newArr)

    //     return (
    //         <>
    //         { newArr.map( (num, index) => (
    //             <Form.Item
    //                 name={ num }
    //                 label={ num }
    //                 hasFeedback
    //             >
    //                 <Row>
    //                     <Select key={ index } labelInValue placeholder={`Please enter ${ num }`} onSelect={ onSelect }>
    //                         { newArr.map( (num, index) => {
    //                             return <Option key={ index } value={ num }>{ num }</Option>
    //                         }) }
    //                     </Select>
    //                 </Row>
    //             </Form.Item>
    //         ))}
    //         </>
    //     )
    // }

    // --------------------------------------------------------------------- //
    // --------------------------------------------------------------------- //
    // --------------------------------------------------------------------- //

	return (
        <Form
            name="validate_other"
            { ...formItemLayout }
            textAlign="center"
            style={ buttonVisible ? { height: '500px' } : { height: '100%' }}
        >
			<Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
            { checked && <Button htmlType="submit" variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.45rem' }}>Create Transactions</Button> }
            <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
			    <input type="file" onChange={ onChange } ref={ hiddenFileInput }  style={{ display: 'none' }} />
                { buttonVisible ? showButtons() : <Title level={3}>Please map the headings</Title>}
            </Form.Item>
            {/* <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="mt-5">
                    <Row>
                        { visible && showTableHeaders(createTable) }
                    </Row>
            </Form.Item> */}
		</Form>
	)

}
export default FileUploadForm
