import React from 'react'
import { Card, Table, Form, Select, Button, Checkbox, Row, Col, Typography, Input } from 'antd';
import { CloseCircleOutlined, PlusCircleOutlined, DownCircleOutlined } from '@ant-design/icons'
import Flex from 'components/shared-components/Flex'
import ConditionalElem from './ConditionalElem'

const { Title } = Typography;
const { Option } = Select;
const { TextArea } = Input

function campaignAssigment(props) {
    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    };

    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    };

    const renderCampaignName = () => {
        const names = ['AXIS Bank', 'ICICI Bank', 'RBL Bank', 'TVS', 'SBI Bank', 'IDFC Bank', 'Bajaj Auto Finance', 'TCL', 'CBD', 'ENBD', 'TCHFL', 'Kotak Mahindra', 'Avanse']

        return (
            <>
                {names.map( name => (
                    <Option value={ name.split(' ')[0].toLowerCase() }>{ name }</Option>
                ))}    
            </>   
        )
    }

    const selectCampaignOption = value => {
        console.log(`${value} has been selected`)
    }

    // const renderCampaignId = () => {
    //     const ids = []

    //     return (
    //         <>
    //             {ids.map( id => (
    //                 <Option value={ id.split(' ')[0].toLowerCase() }>{ id }</Option>
    //             ))}    
    //         </>   
    //     )
    // }

    return (
        <Card>
            <Title style={{ textAlign: 'center', fontSize: '2rem', marginTop: '2%', marginBottom: '4%' }}>Campaign Assignment</Title>
            <Form
                name="validate_other"
                {...formItemLayout}
                onFinish={onFinish}
                textAlign="center"
                initialValues={{ rate: 3.5 }}
            >
                <Form.Item style={{ margin: '0', position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
                    <Flex mobileFlex={false}>
                        <div className="mb-3 mr-3">
                            <Button type="primary" onClick={() => props.history.push("/app/home")}><span style={{ marginRight: '0.5rem' }}><CloseCircleOutlined /></span>Close</Button>
                        </div>
                    </Flex>
                </Form.Item>
                <Form.Item
                    name="select"
                    label="Campaign Name"
                    hasFeedback
                    rules={[{ required: true, message: 'Please select a campaign name' }]}
                >
                    <Select placeholder="Please select a campaign name" onChange={ selectCampaignOption }>
                        { renderCampaignName() }
                    </Select>
                </Form.Item>
                {/* <Form.Item
                    name="select"
                    label="Campaign ID"
                    hasFeedback
                    rules={[{ required: true, message: 'Please select a campaign ID' }]}
                >
                    <Select placeholder="Please select a campaign ID">
                        { renderCampaignId() }
                    </Select>
                </Form.Item> */}
                <ConditionalElem />
            </Form>
        </Card>
    )
}

export default campaignAssigment
