import React, { useState } from 'react'
import { Button, Col, Input, Row, Typography } from 'antd'
import { SearchOutlined, FilterOutlined } from '@ant-design/icons';
import InnerAppLayout from 'layouts/inner-app-layout';
import ChatContent from './ChatContent';
import ChatMenu from './ChatMenu';
import ChatData from "assets/data/chat.data.json"

const { Title } = Typography

const Helpdesk = props => {

    const [ list, setList ] = useState(ChatData);

    const searchOnChange = e => {
		const query = e.target.value;
		const data = ChatData.filter(item => {
			return query === '' ? item : item.name.toLowerCase().includes(query)
		})
		setList(data)
	}

	return (
        <div>
            <Row>
                <Col>
                    <Title level={ 2 }>Conversations</Title>
                </Col>
                <Col>
                    <Input
                        placeholder="Search" 
                        onChange={ searchOnChange }
                        prefix={
                            <SearchOutlined className="font-size-sm mr-2"/>
                        }
                        className="ml-3"
                        style={{ height: '2rem' }}
                    />
                </Col>
                <Col>
                    <Button 
                        className="ml-4" 
                        style={{ height: '2rem', paddingTop: '5px', backgroundColor: '#ecf0f1' }}
                    >
                        <FilterOutlined />
                        Filter
                    </Button>
                </Col>
            </Row>
            <div className="chat">
                <InnerAppLayout 
                    sideContent={ <ChatMenu { ...props }/> }
                    mainContent={ <ChatContent { ...props }/> }
                    sideContentWidth={ 350 }
                    sideContentGutter={ false }
                    border
                />
            </div>
        </div>
	)
}

export default Helpdesk
