import React, { useState } from 'react'
import { message, Typography, Form, Button } from 'antd'
import axios from 'axios'
import { FILE_UPLOAD_URL } from 'constants/ApiConstant'

const { Title } = Typography

const FileUploadForm = props => {
	const [ file, setFile ] = useState(null)

    const onSubmit = async (e) => {
        e.preventDefault() 
        let res = await uploadFile(file);
        console.log(res.data);
	}

    const onChange = e => {
        setFile(e.target.files[0])
	}

    const uploadFile = async (file) => {

        const formData = new FormData();

        formData.append('file', file)

        let res =  await axios.post(FILE_UPLOAD_URL, formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
		})
		res.then( () => message.success(`Your ${file.name} was uploaded successfully.`) )
		.catch( err => message.error(`A ${err.status} error occured. Please try again.`) )

		console.log(res)
		return res
	}

	return (
		<form onSubmit={ onSubmit }>
			<Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1%' }}>
                <Title level={1}>Upload Transactions</Title>
            </Form.Item>
			<div class="box__input">
				<input class="box__file" type="file" name="files[]" id="file" data-multiple-caption="{count} files selected" multiple />
				<label for="file"><strong>Choose a file</strong><span class="box__dragndrop"> or drag it here</span>.</label>
				<button class="box__button" type="submit">Upload</button>
			</div>
			<div class="box__uploading">Uploadingâ€¦</div>
			<div class="box__success">Done!</div>
			<div class="box__error">Error! <span></span>.</div>
		</form>
	)

}
export default FileUploadForm
