            campaign_id: 4,
            chatCount: 10,
            chatDuration: '1:05:15',
            customerName: 'Sumeet',
            customerPhone: '99XXXX7008',
            loanAccNo: 'LPXXXX1234',
            chatInTime: '13:30:00',
            chatOutTime: '13:34:55',
            totalChatDuration: '0:04:55',
            botTime: '0:02:45',
            language: ['English', 'Hindi', 'Tamil', 'Telugu', 'Gujarati']
        },
        {
            key: '3',
            name: 'Sourabh Choudhary',
            agent_id: 3,
            campaign_id: 23,
            chatCount: 2,
            chatDuration: '0:02:45',
            customerName: 'Kapil',
            customerPhone: '99XXXX7008',
            loanAccNo: 'LPXXXX1234',
            chatInTime: '12:32:00',
            chatOutTime: '12:32:45',
            totalChatDuration: '0:00:45',
            botTime: '0:02:15',
            language: ['English', 'Gujarati']
        },
        {
            key: '4',
            name: 'Aaina Khan',
            agent_id: 4,
            campaign_id: 1,
            chatCount: 5,
            chatDuration: '1:05:10',
            customerName: 'Namita',
            customerPhone: '99XXXX7008',
            loanAccNo: 'LPXXXX1234',
            chatInTime: '09:00:00',
            chatOutTime: '09:45:45',
            totalChatDuration: '0:05:45',
            botTime: '0:01:10',
            language: ['Hindi', 'Tamil']
        },
        {
            key: '5',
            name: 'Vijay Gupta',
            agent_id: 5,
            campaign_id: 21,
            chatCount: 10,
            chatDuration: '0:30:00',
            customerName: 'Shalini',
