import React, { Component } from 'react'
import Chart from "react-apexcharts";
import { COLORS } from 'constants/ChartConstant';

export class BasicBar extends Component {
	state = {
		series: [{
			data: [450, 400, 295, 70, 340, 580, 190, 100]
		}],
		options: {
			plotOptions: {
				bar: {
					horizontal: true,
				}
			},
			chart: {
				toolbar: {
				  show: true,
				  tools: {
					download: false
				  }
				}
			},
			colors: COLORS[8],
			dataLabels: {
				enabled: false
			},
			xaxis: {
				categories: ['Client Approved', 'Sent for translation', 'SMS received', 'DLT approval', 'Testing (Phase 1)', 'Testing (Phase 2)', 'Testing (Phase 3)', 'Campaign ready']
			}
		}
	};

	render() {
		return (
			<Chart
				options={this.state.options}
				series={this.state.series}
				type="bar"
				height= {215}
				style={{ paddingTop: 0, marginTop: 0 }}
			/>
		)
	}
}

export default BasicBar
