import React, { useState } from 'react';
import { Card, Collapse, Typography, Button, Tooltip, Form } from 'antd'
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import { SaveOutlined } from '@ant-design/icons'
import NavSearch  from './NavSearch';
import SearchInput from './NavSearch/SearchInput.js'

import { connect } from "react-redux";
import { Menu, Layout } from "antd";

import { toggleCollapsedNav, onMobileNavToggle } from 'redux/actions/Theme';
import { NAV_TYPE_TOP, SIDE_NAV_COLLAPSED_WIDTH, SIDE_NAV_WIDTH } from 'constants/ThemeConstant';
import utils from 'utils'

const { Title } = Typography
const { Panel } = Collapse

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        flexGrow: 1
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: '25ch',
    },
}));

function callback(key) {
    console.log(key);
}

export default function DialogFlow(props) {
    const classes = useStyles();
    const mode = utils.getColorContrast(headerNavColor)
    const { navCollapsed, mobileNav, navType, headerNavColor, toggleCollapsedNav, onMobileNavToggle, isMobile } = props;
    const [searchActive, setSearchActive] = useState(false)

    const onSearchActive = () => {
        setSearchActive(true)
    }

    const onSearchClose = () => {
        setSearchActive(false)
    }

    const onToggle = () => {
        if(!isMobile) {
            toggleCollapsedNav(!navCollapsed)
        } else {
            onMobileNavToggle(!mobileNav)
        }
    }

    return (
        <Card>
            <Title style={{ fontSize: '2.5rem', textAlign: 'center', marginBottom: '2%' }}>Dialog Flow</Title>
            <form className={classes.root} noValidate autoComplete="off">
                <Grid item xs={11}>
                    <TextField id="intent" label="Intent" variant="outlined" required style={{ width: '100%', fontSize: '2rem' }} />
                </Grid>
                <Grid item xs={1}>
                    <Button type="primary" icon={<SaveOutlined style={{ fontSize: '1.75rem' }} />} style={{ marginLeft: '10px', height: '100%', width: '100%' }} />
                </Grid>
                <hr />
                <Grid item xs={12} style={{ marginTop: '5%' }}>
                    <Collapse onChange={callback} ghost>
                        <Panel header="Training Phrases" key="1">

                        </Panel>
                    </Collapse>
                </Grid>
                <Grid item xs={12} style={{ marginTop: '5%' }}>
                    <Collapse onChange={callback} ghost>
                        <Panel header="Responses" key="2">
                            <Form.Item key="1" onClick={() => {onSearchActive()}}>
                                <SearchOutlined />
                            </Form.Item>
                            :
                            <Form.Item key="1" style={{cursor: 'auto'}}>
                                <SearchInput mode={mode} isMobile={isMobile} />
                            </Form.Item>
                        </Panel>
                    </Collapse>
                </Grid>
            </form>
        </Card>
    );
}
