import React from 'react'
import { Input, Card, Typography, Form, Checkbox } from 'antd'
import { Link } from 'react-router-dom'
import axios from 'axios'

const { Search } = Input
const { Title, Paragraph } = Typography

export default function UniversalSearch() {

    const onSearch = async value => {

        let emptyArray = []

        await axios({
            method: 'get',
            url: `https://68ef17ae206a41b485fa164d62a92012.us-east-1.aws.found.io:9243/sc_transaction_demo_v2/_search?q=${value}&size=10000`,
            auth: {
                username: 'sp_es_write',
                password: 'JxpZzs8hRV4X7'
            }
        })
        .then( res => { 
            res.data.hits.hits.map( row => emptyArray.push(row._source) )
            console.log(emptyArray)
            // setEsData(emptyArray)
        })
    }

    function onChange(e) {
        console.log(`checked = ${e.target.checked}`);
    }

    return (
        <Card>
            <Form>
                <Form.Item justify="center" style={{ textAlign: 'center', marginTop: '1.5%' }}>
                    <Title>Spocto Content Search</Title>
                </Form.Item>
                <Form.Item>
                    <Search 
                        placeholder="Input Search Text" 
                        onSearch={ onSearch } 
                        style={{ width: '100%' }}                     
                        allowClear
                        enterButton
                        size="large" 
                    />
                    <Paragraph className="mt-2 ml-2" style={{ fontSize: '0.7rem'}}>Type Client Name, Language, Product, Category etc... e.g. ICICI Bank, Axis Bank or English, Hindi or Loan, Credit Card or Basic, Detailed</Paragraph>
                    <Paragraph className="mt-2 ml-2"><Checkbox onChange={ onChange } />Please check this box to indicate that you are authorized to use Spocto Content Search tool and agree to the <Link>terms and condition</Link>.</Paragraph>
                </Form.Item>
            </Form>
        </Card>
    )
}
