import React, { useState, useRef } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles';
import { Typography, Form, Button, Tag, Row, Col, Card, message, Select, Divider, Input } from 'antd'
import { CloudUploadOutlined, LeftCircleOutlined, RightCircleOutlined, PlusOutlined } from '@ant-design/icons'
import axios from 'axios'
import readXlsxFile from 'read-excel-file'
import { API_LOCAL_BASE_URL, API_LOCAL_DATA_POST_URL, } from 'constants/ApiConstant'
import { StyledButton, StyledNavButton } from 'constants/StyledComponents'
import { MONTHS } from 'constants/DateConstant'
import { APP_PREFIX_PATH } from 'configs/AppConfig'
import { getLangColor, getCustomerColor, getChannelColor, getTypeColor, getProductColor, getSegmentColor } from 'constants/TagColor'

const { Title } = Typography
const { Option } = Select

let index = 0

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

const FileUploadForm = props => {

    // ---------------------------- VARIABLE ASSIGNMENT ---------------------------- //

    // let fileReader;

    let newArr = []

    let checkedLanguages = []
    // let selectedOptions = []

    const [ file, setFile ] = useState(null)
    // const [ rows, setRows ] = useState(null)
    const [ visible, setVisible ] = useState(false)
    // const [ show, setShow ] = useState(false)
    const [ buttonVisible, setButtonVisible ] = useState(true)
    const [ checked, setChecked ] = useState(false)
    const [ createTable, setCreateTable ] = useState([])
    const [ customer, setCustomer ] = useState()
    const [ channel, setChannel ] = useState()
    const [ product, setProduct ] = useState()
    const [ segment, setSegment ] = useState()
    const [ languages, setLanguages ] = useState([])
    const [ addName, setAddName ] = useState('')
    const [ clients, setClients ] = useState(['Axis Bank', 'ICICI Bank', 'RBL', 'TVS', 'SBI', 'IDFC', 'Bajaj', 'TCL', 'CBD', 'ENBD', 'TCHFL', 'Kotak', 'Avanse'])

    // Create a reference to the hidden file input element
    const hiddenFileInput = useRef(null);

    const history = useHistory()

    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    }; 

    const map = {
        'Product': 'product',
        'Segment': 'segment',
        'Type': 'type',
        'Level': 'level',
        'Category': 'category',
        'Content': 'content',
        'Vendor': 'vendor',
        'Languages': 'languages',
        'Status': 'status'
    }

    // --------------------------------------------------------------------- //
    // ------------------------ FUNCTION ASSIGNMENT ------------------------ //
    // --------------------------------------------------------------------- //

    const showButtons = () => {

        return (
            <>
                <Card>
                    <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginTop: 0, marginBottom: 0, paddingtop: 0, paddingTop: 0 }}>
                        <Row gutter={16}>
                            <Col span={8}>
                                { renderClients() }
                            </Col>
                            <Col span={8}>
                                { renderChannels() }
                            </Col>
                            <Col span={8}>
                                { renderProducts() }
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Col span={8}>
                                { renderSegments() }
                            </Col>
                            <Col span={16}>
                                { renderLanguages() }
                            </Col>
                        </Row>
                    </Form.Item>
                </Card>
                <StyledButton onClick={ handleClick } style={{ marginTop: '5%' }}><CloudUploadOutlined style={{ marginRight: '2%' }} /> Upload File</StyledButton>
                <Row style={{ marginTop: '7.5%', display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
                    <StyledNavButton style={{ marginRight: '3%'}}><LeftCircleOutlined style={{ marginRight: '2%' }} />Go to recommendations</StyledNavButton>
                    <Link to={`${APP_PREFIX_PATH}/journeys/journey-ivr/transaction`}>
                        <StyledNavButton>Go to transactions<RightCircleOutlined style={{ marginLeft: '2%' }} /></StyledNavButton>
                    </Link>
                </Row>
            </>
        )
    }

    const handleClick = () => {
        hiddenFileInput.current.click()
    }

    const onChange = async (e) => {
        e.preventDefault()

        let tableArray = []
        const formData = new FormData()

        let todayDate = new Date()
        let formattedDay = `${ todayDate.getDate() > 3 && todayDate.getDate() < 21 ? `${todayDate.getDate()}th` : todayDate.getDate() in [1, 21, 31] ? `${todayDate.getDate()}st` : todayDate.getDate() in [2, 22] ? `${todayDate.getDate()}nd` : `${todayDate.getDate()}rd` }`
        let formattedTime = `${todayDate.getHours()}:${todayDate.getMinutes() < 10 ? `0${todayDate.getMinutes()}` : todayDate.getMinutes()} hrs`

        setFile(e.target.files[0])

        await readXlsxFile(e.target.files[0], { map })
        .then( async ({ rows }) => {
            rows.forEach( row => (
                row.customer_name = customer,
                row.channel = channel,
                row.segment = segment,
                row.language = languages,
                row.status = 'Client Approved',
                row.last_updated_on = `${formattedDay} ${MONTHS[todayDate.getMonth()]}, ${todayDate.getFullYear()} at ${formattedTime}`,
                row.last_updated_by = 'Admin',
                row.version = 1
            ))
            formData.append('client_data', rows)
            esData(rows)
            await axios({
                method: 'post',
                baseURL: API_LOCAL_BASE_URL,
                url: API_LOCAL_DATA_POST_URL,
                data: rows
            })
            .then( res => console.log(`Connection with STATUS ${res.status} has been established.`))
            .catch( function(err) {
                if (err.response) {
                    console.log(err.response, err.response.data, err.response.status, err.response.headers)
                } else if (err.request) {
                    console.log(err.request)
                } else {
                    console.log('Error', err.message);
                }
                console.log(err.config);
            })
        })
        .then( 
            // function(res) {
            // for (let i = 0; i < 3; i++) {
            //     tableArray.push(res[i])
            // }
            console.log(tableArray),
            setCreateTable(tableArray),
            setVisible(true),
            setButtonVisible(false)
        )
    }

    const onSelectCustomer = event => setCustomer(event)

    const onSelectChannel = event => setChannel(event)

    const onSelectProduct = event => setProduct(event)

    const onSelectSegment = event => setSegment(event)

    const onSelectLanguages = event => setLanguages(event)

    const onNameChange = event => {
        setAddName(event.target.value)
    }

    const onShowTableSelect = () => {
        console.log('Hurray!')
    }

    const renderClients = () => {

        const addItem = () => {
            console.log('addItem')
            setClients([ ...clients, addName || `New customer ${index++}`])
            setAddName('')
        }

        return (
            <>
                <Form.Item name="client" label="Client" rules={[{ required: true, message: 'Please select a client' }]}>
                    <Select 
                        showArrow 
                        style={{ width: '100%' }}
                        onChange={ onSelectCustomer }
                        dropdownRender={ menu => (
                            <div>
                                { menu }
                                <Divider style={{ margin: '4px 0' }} />
                                    <div style={{ display: 'flex', flexWrap: 'nowrap', padding: 8 }}>
                                        <Input style={{ flex: 'auto' }} value={ addName } onChange={ onNameChange } />
                                        <a
                                            style={{ flex: 'none', padding: '8px', display: 'block', cursor: 'pointer' }}
                                            onClick={ addItem }
                                        >
                                            <PlusOutlined />
                                        </a>
                                    </div>
                            </div>
                        )}
                    >
                        { clients.map( client => (
                            <Option value={ client } style={{ lineHeight: '32px', width: '100%' }}>
                                <Tag color={ getCustomerColor(client) }>{ client }</Tag>
                            </Option>
                        )) }
                    </Select>
                </Form.Item>  
            </> 
        )
    }

    const renderChannels = () => {
        const channels = ['smartVoice', 'smartText', 'smartEmail']

        return (
            <>
                <Form.Item name="channel" label="Channel" rules={[{ required: true, message: 'Please select a channel' }]}>
                    <Select showArrow style={{ width: '100%' }} onChange={ onSelectChannel }>
                        { channels.map( channel => (
                            <Option value={ channel } style={{ lineHeight: '32px', width: '100%' }}>
                                <Tag color={ getChannelColor(channel) }>{ channel }</Tag>
                            </Option>
                        )) }
                    </Select>
                </Form.Item>
            </>
        )
    }

    const renderProducts = () => {
        const products = ['MFI', 'Loan', 'Credit Card', 'Auto Loan', 'Agri Loan', 'Tractor Loan', 'EMI on Debit Card', 'CD-SNC', 'Common Borrwers', 'Pay Later']

        return (
            <>
                <Form.Item name="product" label="Product">
                    <Select showArrow style={{ width: '100%' }} onChange={ onSelectProduct }>
                        { products.map( product => (
                            <Option value={ product } style={{ lineHeight: '32px', width: '100%' }}>
                                <Tag color={ getProductColor(product) }>{ product }</Tag>
                            </Option>
                        )) }
                    </Select>
                </Form.Item>
            </>
        )
    }

    const renderLanguages = () => {
        const languages = ['English', 'Hindi', 'Tamil', 'Telugu', 'Kannada', 'Marathi', 'Gujarati', 'Bengali', 'Punjabi', 'Malayalam']

        return (
            <>
                <Form.Item name="language" label="Languages" rules={[{ required: true, message: 'Please select the languages' }]}>
                    <Select mode="multiple" showArrow style={{ width: '100%' }} onChange={ onSelectLanguages }>
                        { languages.map( language => (
                            <Option value={ language } style={{ lineHeight: '32px', width: '100%' }}>
                                <Tag color={ getLangColor(language) }>{ language }</Tag>
                            </Option>
                        )) }
                    </Select>
                </Form.Item>
            </>
        )
    }

    const renderSegments = () => {
        const segments = ['Pre Due', 'Post Due', 'Bucket X', 'NPA', 'W/Off', 'Phase 1', 'Phase 2', 'Phase 3', 'Phase 4 (Pre 90)', 'Phase 4 (90+)']

        return (
            <>
                <Form.Item name="segment" label="Segment">
                    <Select showArrow style={{ width: '100%' }} onChange={ onSelectSegment }>
                        { segments.map( segment => (
                            <Option value={ segment } style={{ lineHeight: '32px', width: '100%' }}>
                                <Tag color={ getSegmentColor(segment) }>{ segment }</Tag>
                            </Option>
                        )) }
                    </Select>
                </Form.Item>
            </>
        )
    }


    const showTableHeaders = array => {

        newArr = array[0]

        console.log(newArr)

        return (
            <>
            { newArr.map( (num, index) => (
                <Form.Item
                    name={ num }
                    label={ num }
                    hasFeedback
                >
                    <Row>
                        <Select key={ index } labelInValue placeholder={`Please enter ${ num }`} onSelect={ onShowTableSelect }>
                            { newArr.map( (num, index) => {
                                return <Option key={ index } value={ num }>{ num }</Option>
                            }) }
                        </Select>
                    </Row>
                </Form.Item>
            ))}
            </>
        )
    }

    // --------------------------------------------------------------------- //
    // --------------------------------------------------------------------- //
    // --------------------------------------------------------------------- //

	return (
        <Form
            name="validate_other"
            { ...formItemLayout }
            textAlign="center"
            style={ buttonVisible ? { height: '500px' } : { height: '100%' }}
        >
            { checked && <Button htmlType="submit" variant="contained" type="primary" style={{ width: '100%', height: '3rem', fontSize: '1.45rem' }}>Create Transactions</Button> }
            <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
			    <input type="file" onChange={ onChange } ref={ hiddenFileInput }  style={{ display: 'none' }} />
                { buttonVisible ? showButtons() : <Title level={3}>Please map the headings</Title>}
            </Form.Item>
            <Form.Item style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="mt-5">
                    <Row>
                        { visible && showTableHeaders(createTable) }
                    </Row>
            </Form.Item>
		</Form>
	)

}
