/* eslint-disable no-unused-vars */
import React, {useState} from 'react'
import { Card, Table, Select, Input, Button, Badge, Menu, Tag, Pagination } from 'antd';
import OrderListData from 'assets/data/order-list.data.json'
import { EyeOutlined, CloudDownloadOutlined, SearchOutlined, PlusCircleOutlined } from '@ant-design/icons';
import EllipsisDropdown from 'components/shared-components/EllipsisDropdown';
import Flex from 'components/shared-components/Flex'
import NumberFormat from 'react-number-format';
import moment from 'moment'; 
import { DATE_FORMAT_DD_MM_YYYY } from 'constants/DateConstant'
import utils from 'utils'

const { Option } = Select

const getTransactionStatus = status => {
	if (status === 'Client Approved') return 'volcano'
	if (status === 'IVR') return 'ivr'
	if (status === 'Testing') return 'testing'
	if (status === 'Approved') return 'approved'
	return ''
}

const getUserStatus = status => {
    if (status === 'Admin') return 'red'
    if (status === 'Campaign Team') return 'blue'
    if (status === 'DevOps Team') return 'cyan'
}

const getLangColor = lang => {
	switch(lang) {
		case ('English'): return 'blue'
		case ('Hindi'): return 'cyan'
		case ('Tamil'): return 'gold'
		case ('Telugu'): return 'red'
		case ('Kannada'): return 'volcano'
		case ('Gujarati'): return 'purple'
		case ('Marathi'): return 'magenta'
	}
}

const getCustomerColor = customer => {
	switch(customer) {
		case "AXIS Bank": return 'blue' 
		case "ICICI Bank": return 'cyan'
		case "RBL": return 'red'
		case "TVS": return 'purple'
		case "IDFC": return 'volcano'
		case "Bajaj": return 'gold'
		case "TCL": return 'lime'
		case "TCHFL": return 'magenta'
	}
}

const getProductColor = product => {
	switch(product) {
		case "MFI": return 'blue'
		case "Loan": return 'cyan'
		case "Credit Card": return 'red'
		case "Agri Loan": return 'purple'
		case "Tractor Loan": return 'magenta'
		case "EMI on Debit Card": return 'volcano'
		case "CD-SNC": return 'gold'
		case "Common Borrowers": return 'lime'
		case "Pay Later": return 'geekblue'
		default: return 'green'
	}
}

const transactionStatusList = ['Client Approved', 'IVR', 'Testing', 'Approved']

export default function Dashboard({ esData }) {

	console.log(esData)
	console.log(OrderListData)

	const [list, setList] = useState(esData)
	const [selectedRows, setSelectedRows] = useState([])
	const [selectedRowKeys, setSelectedRowKeys] = useState([])

	const handleShowStatus = value => {
		if (value !== 'All') {
			const key = 'transactionStatus'
			const data = utils.filterArray(esData, key, value)
			setList(data)
		} else {
			setList(esData)
		}
	}

	const dropdownMenu = row => (
		<Menu>
			<Menu.Item>
				<Flex alignItems="center">
					<EyeOutlined />
					<span className="ml-2">View Details</span>
				</Flex>
			</Menu.Item>
			<Menu.Item>
				<Flex alignItems="center">
					<PlusCircleOutlined />
					<span className="ml-2">Add to remark</span>
				</Flex>
			</Menu.Item>
		</Menu>
	);

	const tableColumns = [
		{
			title: 'ID',
			dataIndex: 'id',
			key: 'id'
		},
		{
			title: 'Channel',
			dataIndex: 'channel',
			render: (_, record) => (
				<div className="d-flex">
					<Tag color={ record.channel == 'smartVOICE' ? 'lime' : record.channel == 'smartTEXT' ? 'cyan' : 'magenta'}>{ record.channel }</Tag>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'channel'),
			key: 'channel'
        },
		{
			title: 'Customer Name',
			dataIndex: 'customer_name',
			render: (_, record) => (
				<div className="d-flex">
					<Tag color={ getCustomerColor(record.customer_name) } style={{ letterSpacing: '1px' }}>{ record.customer_name }</Tag>
				</div>
			),
			sorter: (a, b) => utils.antdTableSorter(a, b, 'customer_name'),
			key: 'customer_name'
        },
		{
			title: 'Content Name',
			dataIndex: 'content_name',
			render: (_, record) => (
				<div className="d-flex">
