			key: 'actions'
		}
	];

	const rowSelection = {
		onChange: (key, rows) => {
			setSelectedRows(rows)
			setSelectedRowKeys(key)
		}
	};

	const onSearch = e => {
		const value = e.currentTarget.value
		const searchArray = e.currentTarget.value? list : OrderListData
		const data = utils.wildCardSearch(searchArray, value)
		setList(data)
		setSelectedRowKeys([])
	}

	return (
		<Card>
			<Flex alignItems="center" justifyContent="between" mobileFlex={false}>
				<Flex className="mb-1" mobileFlex={false}>
					<div className="mr-md-3 mb-3">
						<Input placeholder="Search" prefix={<SearchOutlined />} onChange={e => onSearch(e)}/>
					</div>
					<div className="mb-3">
						<Select 
							defaultValue="All" 
							className="w-100" 
							style={{ minWidth: 180 }} 
							onChange={ handleShowStatus } 
							placeholder="Status"
						>
							<Option value="All">Transactions </Option>
							{transactionStatusList.map(elm => <Option key={elm} value={elm}>{elm}</Option>)}
						</Select>
					</div>
				</Flex>
			</Flex>
                <Flex alignItems="center" mobileFlex={false}>
					<div className="mb-3 mr-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Filter</Button>
                    </div>
					<div className="mb-3 mr-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><PlusCircleOutlined /></span>Add Sort</Button>
                    </div>
                    <div className="mb-3">
                        <Button type="primary"><span style={{ marginRight: '0.5rem' }}><CloudDownloadOutlined /></span>Download Selected</Button>
                    </div>
                </Flex>
			<div className="table-responsive">
                <Table 
                    bordered
					columns={tableColumns} 
					dataSource={list} 
					rowKey='id' 
					rowSelection={{
						selectedRowKeys: selectedRowKeys,
						type: 'checkbox',
						preserveSelectedRowKeys: false,
						...rowSelection,
                    }}
                    scroll={{ x: 2000 }}
                    pagination={{ pageSize: 10, pageSizeOptions: [5, 10, 15, 20] }}
                    showSizeChanger
                    showQuickJumper
                    responsive={true}
				/>
			</div>
		</Card>
	)
